sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var grSI = new GlideRecord('sys_dictionary');
	grSI.addQuery('element','=',current.element);
	grSI.query();
	if (grSI.next())
	{
		var strTableScope = GetStringValue(grSI.sys_scope);
		if (current.sys_scope != strTableScope)
		{
			current.sys_scope = strTableScope;
		}
	}

})(current, previous);
