sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ConfigurationItemServiceNowAppInsertOrUpdate(strInstanceName, vApp, vExpected)
	{
		function TestCase_ConfigurationItemServiceNowAppInsertOrUpdate_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			if (IsValidSysId(vResult) == false)
			{
				return false;
			}
			var grCI = GetRecord('x_snc_devtools_cmdb_ci_appl_servicenow',vResult);
			if (IsValidRecord(grCI) == false)
			{
				return false;
			}
			var strExpectedName = GetValue(vExpected,'name');
			if (strExpectedName != grCI.name)
			{
				return false;
			}
			return true;
		}

		test.TestCase(
			'Function ConfigurationItemServiceNowAppInsertOrUpdate()',
			ConfigurationItemServiceNowAppInsertOrUpdate,
			{
				'strInstanceName': strInstanceName,
				'vApp': vApp,
			},
			vExpected,
			TestCase_ConfigurationItemServiceNowAppInsertOrUpdate_CheckResult);
	}

	TestCase_ConfigurationItemServiceNowAppInsertOrUpdate(null,null,false);
	grApp = GetAppRecord('x_snc_devtools');
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdate(
		'testtesttest',
		grApp,
		{
			name: grApp.name + '@' + 'testtesttest'
		});
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
