sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var grAppDevTools = GetAppRecord('x_snc_devtools');
	
	test.Log('Creating a linter check that ALWAYS produces a finding:');
	var grLinterCheck = new GlideRecord('scan_linter_check');
	grLinterCheck.initialize();
	grLinterCheck.name = 'A Linter Check that always triggers a finding';
	grLinterCheck.script = '(function (engine) { engine.finding.incrementWithNode(engine.rootNode); })(engine);';
	var strLinterCheckSysId = GlideRecordInsert(grLinterCheck);
	test.Log(RenderValue(grLinterCheck));
	test.Log('\n')
	
	test.Log('Creating a Flow Variable has no relevant script fields - so the business rule should prevent the check from running!');
	var grFlowVariable = new GlideRecord('sys_hub_flow_variable');
	grFlowVariable.initialize();
	grFlowVariable.sys_scope = grAppDevTools.sys_id;
	var strFlowVariableSysId = GlideRecordInsert(grFlowVariable);
	test.Log(RenderValue(grFlowVariable));
	test.Log('\n')

	test.Log('\nThe business rule should be active and prevent the check from executing on the record.\n');

	Sleep(5);
	var result = InstanceScanCheckOnRecord(grLinterCheck.sys_id, 'sys_hub_flow_variable', strFlowVariableSysId);
	test.Log('InstanceScanCheckOnRecord returned: '+RenderValue(result));
	
	var aFindings = GetArrayValue(GetValue(result,'findings'));
	if (aFindings.length != 0)
	{
		test.Log('FAILED: Zero findings expected!');
		test.SetResult(false);
	}
	
	test.Log('\nNow, the business rule is set inactive, so the check is expected to execute and produce a finding.\n');
	RecordSetValue('sys_script','b7fad21a1be06d105b76777d8b4bcbbe','active','false');

	Sleep(5);
	result = InstanceScanCheckOnRecord(grLinterCheck.sys_id, 'sys_hub_flow_variable', strFlowVariableSysId);
	test.Log('InstanceScanCheckOnRecord returned: '+RenderValue(result));

	var aFindings = GetArrayValue(GetValue(result,'findings'));
	if (aFindings.length != 1)
	{
		test.Log('FAILED: Exactly one finding expected!');
		test.SetResult(false);
	}

	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
