sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	test.TestCase_GetRecord('','',false,false);
	test.TestCase_GetRecord('x_snc_devtools_test','',false,false);
	
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'ATF_DEVTOOLS_ITEM_1';
	grTest.value = 'Hello World';
	var strTestSysId = grTest.insert();
	
	test.TestCase_GetRecord('',strTestSysId,false,false);
	test.TestCase_GetRecord('x_snc_devtools_test',strTestSysId,false,true);
	test.TestCase_GetRecord('x_snc_devtools_test',grTest,false,true);
	
	// sys_export_set_run does not allow cross scope access
	// But even if we do NOT allow cross scope record loading, GetRecord returns the record. Really strange...
	//TestCase_GetRecord('sys_export_set_run',GetRecordFromTestStep('2e9442811bcb7450edf3642aab4bcbf7'),true,false);
	
	var grExportSetRun = new GlideRecord('sys_export_set_run');
	grExportSetRun.initialize();
	grExportSetRun.state = 'cancelled';
	var strExportSetRunSysId = GlideRecordInsert(grExportSetRun);	
	
	test.TestCase_GetRecord('sys_export_set_run',strExportSetRunSysId,false,true);
		
	test.TestCase_GetRecord('sys_scope','global',false,true);
	test.TestCase_GetRecord('sys_scope','ebe42450739b13005754660c4cf6a7b1',false,true); // Incident ATF Tests
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
