sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_InstallApp(vApp, bStopOnError, vExpected)
	{
		test.TestCase(
			'Function InstallApp()',
			InstallApp,
			{
				'App': vApp,
				'StopOnError': bStopOnError
			},
			vExpected,
			null);
	}


	TestCase_InstallApp(null,false,false);

	var grApp1 = new GlideRecord('sys_app');
	grApp1.initialize();
	grApp1.name = 'App1';
	grApp1.scope = 'x_snc_app1';
	var strApp1SysId = GlideRecordInsert(grApp1);
	
	test.Log('No AppInstall function in App1 means nothing to do, hence installation should be successful.');
	TestCase_InstallApp(grApp1,false,true);
	
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.sys_scope = strApp1SysId;
	grSI.name = 'AppInstall';
	grSI.api_name = 'x_snc_app1.AppInstall';
	grSI.access = 'public';
	grSI.script = 'function AppInstall() { gs.info("App1 AppInstall returns true"); return true; }';
	var strAppInstall1SysId = GlideRecordInsert(grSI);
	
	test.Log('The App1 AppInstall function returns true, hence installation should be successful.');
	TestCase_InstallApp(grApp1,false,true);
	
	
	var grApp2 = new GlideRecord('sys_app');
	grApp2.initialize();
	grApp2.name = 'App2';
	grApp2.scope = 'x_snc_app2';
	var strApp2SysId = GlideRecordInsert(grApp2);

	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.sys_scope = strApp2SysId;
	grSI.name = 'AppInstall';
	grSI.api_name = 'x_snc_app2.AppInstall';
	grSI.access = 'public';
	grSI.script = 'function AppInstall() { gs.info("App2 AppInstall returns true"); return true; }';
	var strAppInstall2SysId = GlideRecordInsert(grSI);

	test.Log('The App2 AppInstall function returns true, hence installation should be successful.');
	TestCase_InstallApp(grApp2,false,true);
	
	// App1 now depends on App2
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp1SysId;
	grDependency.dependency = strApp2SysId;
	grDependency.name = 'App2';
	grDependency.insert();
	test.Log('App1 and App2 AppInstall() functions return true, so the installation should be sucessful.');
	TestCase_InstallApp(grApp1,false,true);	
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp2SysId;
	grDependency.dependency = strApp1SysId;
	grDependency.name = 'App1';
	var strDependencyCircularSysId = grDependency.insert();
	test.Log('The installation should fail due to the circular dependency between App1 and App2.');
	TestCase_InstallApp(grApp1,false,false);	
	
	RecordDelete('x_snc_devtools_dependency',strDependencyCircularSysId); 
	test.Log('The installation should now pass again as the circular dependency is removed.');
	TestCase_InstallApp(grApp1,false,true);	
	
	
	var grApp3 = new GlideRecord('sys_app');
	grApp3.initialize();
	grApp3.name = 'App3';
	grApp3.scope = 'x_snc_app3';
	var strApp3SysId = GlideRecordInsert(grApp3);

	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.sys_scope = strApp3SysId;
	grSI.name = 'AppInstall';
	grSI.api_name = 'x_snc_app3.AppInstall';
	grSI.access = 'public';
	grSI.script = 'function AppInstall() { gs.info("App3 AppInstall returns false"); return false; }';
	var strAppInstall3SysId = GlideRecordInsert(grSI);
	test.Log('Installation of App3 fails because AppInstall returns false.');
	TestCase_InstallApp(grApp3,false,false);	
	
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp2SysId;
	grDependency.dependency = strApp3SysId;
	grDependency.name = 'App3';
	grDependency.insert();
	test.Log('The installation of App1 fails because it depends on App2 which depends on App3.');
	TestCase_InstallApp(grApp1,false,false);	

	
	
	
	var grApp4 = new GlideRecord('sys_app');
	grApp4.initialize();
	grApp4.name = 'App4';
	grApp4.scope = 'x_snc_app1';
	var strApp4SysId = GlideRecordInsert(grApp4);
	
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.sys_scope = strApp4SysId;
	grSI.name = 'AppInstall';
	grSI.api_name = 'x_snc_app4.AppInstall';
	grSI.access = 'public';
	grSI.script = 'function AppInstall() {'
		+'gs.info("App4 AppInstall returns true");'
		+'var cache = new x_snc_devtools.TransactionCacheAPI("_ATF_DEVTOOLS_INSTALLAPP_");'
		+'cache.SetItem("App4",true);'
		+'return true; }';
	var strAppInstall4SysId = GlideRecordInsert(grSI);
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp4SysId;
	grDependency.dependency = strApp3SysId;
	grDependency.name = 'App3';
	var strDependencySysId = grDependency.insert();
	test.Log('The installation of App4 fails because it depends on App3 - and the AppInstall() function of App4 is NOT called.');
	TestCase_InstallApp(grApp4,true,false);	
	
	var cache = new x_snc_devtools.TransactionCacheAPI('_ATF_DEVTOOLS_INSTALLAPP_');
	if (cache.GetItem('App4') == true)
	{
		test.Log('FAILED: App4 AppInstall() should not have been called!');
		test.SetResult(false);
	}
	
	RecordDelete('x_snc_devtools_dependency',strDependencySysId); 
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp4SysId;
	grDependency.dependency = strApp2SysId;
	grDependency.name = 'App2';
		
	test.Log('App4 is now depending on App2. The installation of App4 should now pass as the toxic dependency to App3 is removed.');
	TestCase_InstallApp(grApp4,true,true);
	if (cache.GetItem('App4') == false)
	{
		test.Log('FAILED: App4 AppInstall() should have been called!');
		test.SetResult(false);
	}	
	

	var grApp5 = new GlideRecord('sys_app');
	grApp5.initialize();
	grApp5.name = 'App5';
	grApp5.scope = 'x_snc_app1';
	var strApp5SysId = GlideRecordInsert(grApp5);

	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = strApp4SysId;
	grDependency.dependency = strApp5SysId;
	grDependency.name = 'App5';
	test.Log('App4 is now also depending on App5 - which does not have an AppInstall() function. The installation of App4 should pass anyway.');
	TestCase_InstallApp(grApp4,true,true);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
