var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;


	function TestCase_GetUserRecord(vInput, vExpected)
	{
		Log('TestCase_GetUserRecord');
		Log('Input                : '+RenderValue(vInput));
		Log('Expected             : '+RenderValue(vExpected));
		var vResult = GetUserRecord(vInput);
		Log('GetType(Result)      : '+GetType(vResult));
		Log('IsValidRecord(Result): '+RenderValue(IsValidRecord(vResult)));
		Log('Result               : '+RenderValue(vResult));

		
		if (vExpected == false)
		{
			if (vResult != vExpected)
			{
				bResult = false;
				Log('Test case FAILED');
			}
		}
		else
		{
			if (IsValidRecord(vResult) != true)
			{
				bResult = false;
				Log('Test case FAILED');
			}
		}
		
		Log('');
	}

	
	TestCase_GetUserRecord('',false);
	TestCase_GetUserRecord('_ATF_DEVTOOLS_DOES_NOT_EXIST',false);
	TestCase_GetUserRecord('12345678901234567890123456789012',false);

	
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	
	var strUser1 = testdata.CreateUser('TEST1',[],false,'test1@example.com');
	
	TestCase_GetUserRecord(strUser1,true);
	TestCase_GetUserRecord('_ATF_DEVTOOLS_TEST1',true);
	TestCase_GetUserRecord('test1@example.com',true);

	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
