sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_UxStyleSetValue(
		strSysId,vValue,
		vKey1, vKey2, vKey3, vKey4, vKey5, vKey6, vKey7, vKey8, vKey9, vKey10,
		vExpected)
	{
		function TestCase_UxStyleSetValue_CheckResult(vInput,vExpected,vResult)
		{
			test.Log('Specified record:');
			var grRecord = GetRecord('sys_ux_style',vInput['strSysId']);
			test.Log(RenderValue(grRecord));
			if (vExpected == true)
			{
				var strStyleJson = GetValue(grRecord,'style');
				var vJsonObject = ParseJson(strStyleJson);
				
				if (GetValue(vJsonObject,vKey1, vKey2, vKey3, vKey4, vKey5, vKey6, vKey7, vKey8, vKey9, vKey10) != vValue)
				{
					test.Log('Unexpected value in json object:');
					test.Log(vJsonObject);
					return false;	
				}
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function UxStyleSetValue()',
			UxStyleSetValue,
			{
				strSysId: strSysId,
				vValue: vValue,
				vKey1: vKey1,
				vKey2: vKey2,
				vKey3: vKey3,
				vKey4: vKey4,
				vKey5: vKey6,
				vKey6: vKey6,
				vKey7: vKey7,
				vKey8: vKey8,
				vKey9: vKey9,
				vKey10: vKey10
			},
			vExpected,
			TestCase_UxStyleSetValue_CheckResult);
	}

	var strUxStyleSysId = 'a197a38107b03010e03948f78ad30059'; // Polaris default colors

	TestCase_UxStyleSetValue(
		null,
		null,
		null,null,null,null,null,null,null,null,null,null,
		false);

	TestCase_UxStyleSetValue(
		strUxStyleSysId,
		'bogus',
		'test',null,null,null,null,null,null,null,null,null,
		true);


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
