sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_CreateUserByEmail(strEmail, aGroups, vExpected)
	{
		function TestCase_CreateUserByEmail_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			if (IsValidSysId(vResult) == false)
			{
				return false;
			}
			test.Log('Created User: '+RenderValue(GetUserRecord(vResult),true));
			
			var aGroups = GetArrayValue(GetValue(vInput,'Groups'));
			if (aGroups.length > 0)
			{
				var grMember = new GlideRecord('sys_user_grmember');
				grMember.addQuery('user','=',vResult);
				grMember.query();
				if (grMember.getRowCount() != aGroups.length)
				{
					test.Log('Unexpected amount of group memberships.');
					return false;
				}
			}
			
			return true;
		}

		test.TestCase(
			'Function CreateUserByEmail()',
			CreateUserByEmail,
			{
				'Email': strEmail,
				'Groups': aGroups,
			},
			vExpected,
			TestCase_CreateUserByEmail_CheckResult);
	}

	TestCase_CreateUserByEmail(null,null,false);
	TestCase_CreateUserByEmail('devtools-atf_test@example.com',null,true);
	TestCase_CreateUserByEmail('john.doe-DEVTOOLS_ATF@example.com',null,true);
	
	var grGroup = new GlideRecord('sys_user_group');
	grGroup.initialize();
	grGroup.name = '_DEVTOOLS_ATF_TEST_GROUP_';
	var strGroup1SysId = GlideRecordInsert(grGroup);
	
	TestCase_CreateUserByEmail('jane.doe@example.com',[strGroup1SysId],true);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
