sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ScriptAutoGenerateDocumentation(strScript, strDescription, vExpected)
	{
		function TestCase_ScriptAutoGenerateDocumentation_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function ScriptAutoGenerateDocumentation()',
			ScriptAutoGenerateDocumentation,
			{
				'strScript': strScript,
				'strDescription': strDescription
			},
			vExpected,
			TestCase_ScriptAutoGenerateDocumentation_CheckResult);
	}

	TestCase_ScriptAutoGenerateDocumentation(null,null,false);
	TestCase_ScriptAutoGenerateDocumentation(
		'function doSomething() {}',
		'This function does not do much',
		'This function does not do much');
	TestCase_ScriptAutoGenerateDocumentation(
		'var myClass = Class.create(); myClass.prototype = { doSomething: function() {} };',
		'My class does not do much!',
		'My class does not do much!\n|\nMethods:\n* doSomething()');
TestCase_ScriptAutoGenerateDocumentation(
		'var myClass = Class.create(); myClass.prototype = { doSomething: function() {} };',
		'My class does not do much!\n|\nMethods:\n* doSomething()',
		'My class does not do much!\n|\nMethods:\n* doSomething()');		
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
