sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);


	function TestCase_GetDependencyListFromTree(dependencytree, vExpected)
	{
		function TestCase_GetDependencyListFromTree_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			var aPackages = GetArrayValue(vResult);
			if (GetValue(vExpected,'count') != aPackages.length)
			{
				test.Log('Unexpected package count.');
				return false;
			}
			if (GetValue(vExpected,'dependency_status_summary') != GetValue(aPackages,aPackages.length-1,'dependency_status_summary'))
			{
				test.Log('Unexpected dependency status summary.');
				return false;
			}
			var strTestAppCompatibleVersion = GetValue(vExpected,'testapp_compatible_version');
			if (strTestAppCompatibleVersion != '')
			{
				for (var nPackage = 0; nPackage < aPackages.length; nPackage++)
				{
					if (GetValue(aPackages[nPackage],'name') == 'TestApp')
					{
						if (strTestAppCompatibleVersion != GetValue(aPackages[nPackage],'compatible_versions'))
						{
							return false;
						}
					}
				}
			}
			return true;
		}

		test.TestCase(
			'Function GetDependencyListFromTree()',
			GetDependencyListFromTree,
			{
				'Dependency Tree': dependencytree
			},
			vExpected,
			TestCase_GetDependencyListFromTree_CheckResult);
	}

	TestCase_GetDependencyListFromTree(null,false);
	TestCase_GetDependencyListFromTree('',false);

	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),{ count: 1, dependency_status_summary: 'ok' });
	
	
	test.Log('Scenario: differing compatible versions (higher first)');
	test.Log('A depends on B');
	test.Log('B depends on TestApp 1.2.0');
	test.Log('A depends on TestApp 1.1.0');

	var tree = {
		name: 'A',
		version: '1.0.0',
		sys_id: '1',
		dependency_status_summary: 'ok',
		_dependencies: [
			{
				name: 'B',
				version: '1.0.0',
				sys_id: '2',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'TestApp',
						version: '1.2.0',
						sys_id: '3',
						compatible_versions: '1.2.0'
					}
				]
			},
			{
				name: 'TestApp',
				version: '1.2.0',
				sys_id: '3',
				compatible_versions: '1.1.0'
			}
		]
	};
	TestCase_GetDependencyListFromTree(tree,{ count: 3, dependency_status_summary: 'ok', testapp_compatible_version: '1.2.0' });	

	
	test.Log('Scenario: differing compatible versions (lower first)');
	test.Log('A depends on TestApp 1.1.0');
	test.Log('A depends on B');
	test.Log('B depends on TestApp 1.2.0');
	

	var tree = {
		name: 'A',
		version: '1.0.0',
		sys_id: '1',
		dependency_status_summary: 'ok',
		_dependencies: [
			{
				name: 'TestApp',
				version: '1.2.0',
				sys_id: '3',
				compatible_versions: '1.1.0'
			},
			{
				name: 'B',
				version: '1.0.0',
				sys_id: '2',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'TestApp',
						version: '1.2.0',
						sys_id: '3',
						compatible_versions: '1.2.0'
					}
				]
			}
		]
	};
	TestCase_GetDependencyListFromTree(tree,{ count: 3, dependency_status_summary: 'ok', testapp_compatible_version: '1.2.0' });	


	test.Log('Scenario: differing incompatible versions (higher first)');
	test.Log('A depends on B');
	test.Log('B depends on TestApp 2.0.0');
	test.Log('A depends on TestApp 1.1.0');

	var tree = {
		name: 'A',
		version: '1.0.0',
		sys_id: '1',
		_dependencies: [
			{
				name: 'B',
				version: '1.0.0',
				sys_id: '2',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'TestApp',
						version: '2.0.0',
						sys_id: '3',
						compatible_versions: '2.0.0'
					}
				]
			},
			{
				name: 'TestApp',
				version: '1.1.0',
				sys_id: '3',
				compatible_versions: '1.1.0'
			}
		]
	};
	TestCase_GetDependencyListFromTree(tree,
		{ count: 3, dependency_status_summary: 'conflicting_versions', testapp_compatible_version: '' });	

	
	test.Log('Scenario: differing incompatible versions (lower first)');
	test.Log('A depends on TestApp 1.1.0');
	test.Log('A depends on B');
	test.Log('B depends on TestApp 2.0.0');
	

	var tree = {
		name: 'A',
		version: '1.0.0',
		sys_id: '1',
		_dependencies: [
			{
				name: 'TestApp',
				version: '1.2.0',
				sys_id: '3',
				compatible_versions: '1.2.0'
			},
			{
				name: 'B',
				version: '1.0.0',
				sys_id: '2',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'TestApp',
						version: '2.0.0',
						sys_id: '3',
						compatible_versions: '2.0.0'
					}
				]
			}
		]
	};
	TestCase_GetDependencyListFromTree(tree,
		{ count: 3, dependency_status_summary: 'conflicting_versions', testapp_compatible_version: '' });		
	

	test.Log('Scenario: ok');	
		
	var grApp = GetAppRecord('x_snc_devtools');

	var grPackagePackage = new GlideRecord('sys_plugins');
	grPackagePackage.addQuery('name','=','Package');
	grPackagePackage.query();
	grPackagePackage.next();

	var grPackageServicePortal = new GlideRecord('sys_plugins');
	grPackageServicePortal.addQuery('name','=','Service Portal - Core');
	grPackageServicePortal.query();
	grPackageServicePortal.next();
	
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = grPackagePackage.sys_id;
	grDependency.name = 'Package';
	grDependency.insert();
	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),{ count: 2, dependency_status_summary: 'ok' });		

	test.Log('Scenario: incompatible versions');	
		
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = grPackageServicePortal.sys_id;
	grDependency.name = 'Service Portal - Core';
	grDependency.versions = '1000.0.0';
	grDependency.insert();
	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),
		{ count: 3, dependency_status_summary: 'incompatible_version' });

	
	test.Log('Scenario: missing');	

	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = GetPseudoSysId();
	grDependency.name = 'Pseudo';
	grDependency.insert();
	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),
		{ count: 4, dependency_status_summary: 'missing' });
	
	test.Log('Scenario: removing duplicates');	
	
	// "Service Portal - Core" now also has a dependency to "Pseudo" - so two different packages depend on the same package and duplicates are removed
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grPackageServicePortal.sys_id;
	grDependency.dependency = GetPseudoSysId();
	grDependency.name = 'Pseudo';
	grDependency.insert();
	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),
		{ count: 4, dependency_status_summary: 'missing' });	

	test.Log('Scenario: circular dependency');
	
	// "Service Portal - Core" now has a dependency to "DevTools" - this would be a circular dependency which is not allowed!  
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grPackageServicePortal.sys_id;
	grDependency.dependency = grApp.sys_id;
	grDependency.name = 'DevTools';
	grDependency.insert();
	TestCase_GetDependencyListFromTree(GetPackageDependencyTree('x_snc_devtools'),
		{ count: 5, dependency_status_summary: 'circular' });


	test.Log('Scenario: nested missing dependency');
	var tree = {
		name: 'Alpha',
		version: '1.0.0',
		sys_id: '1',
		_dependencies: [
			{
				name: 'Beta',
				version: '1.2.0',
				sys_id: '2',
				compatible_versions: '1.2.0'
			},
			{
				name: 'Gamma',
				version: '1.0.0',
				sys_id: '3',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'Delta',
						dependency_status: 'missing',
						sys_id: '4',
						compatible_versions: '2.0.0'
					}
				]
			}
		]
	};

	TestCase_GetDependencyListFromTree(tree,
		{ count: 4, dependency_status_summary: 'missing' });


	test.Log('Scenario: Nested missing and non-missing dependency to the same app');
	var tree = {
		name: 'Alpha',
		version: '1.0.0',
		sys_id: '1',
		_dependencies: [
			{
				name: 'Beta',
				version: '1.2.0',
				sys_id: '2',
				compatible_versions: '1.2.0',
				_dependencies: [
					{
						name: 'Delta',
						version: '2.0.1',
						dependency_status: 'ok',
						sys_id: '4',
						compatible_versions: '2.0.1'
					}
				]
			},
			{
				name: 'Gamma',
				version: '1.0.0',
				sys_id: '3',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'Delta',
						dependency_status: 'missing',
						sys_id: '4',
						compatible_versions: '2.0.0'
					}
				]
			}
		]
	};

	TestCase_GetDependencyListFromTree(tree,
		{ count: 4, dependency_status_summary: 'ok' });

	test.Log('Scenario: Nested missing (more recent version) and non-missing dependency to the same app');
	var tree = {
		name: 'Alpha',
		version: '1.0.0',
		sys_id: '1',
		_dependencies: [
			{
				name: 'Beta',
				version: '1.2.0',
				sys_id: '2',
				compatible_versions: '1.2.0',
				_dependencies: [
					{
						name: 'Delta',
						version: '2.0.1',
						dependency_status: 'ok',
						sys_id: '4',
						compatible_versions: '2.0.1'
					}
				]
			},
			{
				name: 'Gamma',
				version: '1.0.0',
				sys_id: '3',
				compatible_versions: '1.0.0',
				_dependencies: [
					{
						name: 'Delta',
						dependency_status: 'missing',
						sys_id: '4',
						compatible_versions: '2.0.2'
					}
				]
			}
		]
	};

	TestCase_GetDependencyListFromTree(tree,
		{ count: 4, dependency_status_summary: 'missing' });




	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
