sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);

	function TestCase_GetRolesFromUser(strUserSysId, vExpected)
	{
		test.TestCase(
			'GetRolesFromUser()',
			GetRolesFromUser,
			{
				'User SysId': strUserSysId 
			},
			vExpected,
			TestCase_GetRolesFromUser_CheckResult);
	}

	function TestCase_GetRolesFromUser_CheckResult(vInput,vExpected,vResult)
	{
		return (IsEqual(vResult,vExpected,true));
	}
	
	
	test.SetResult(true);

	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	
	var strRoleUser = testdata.CreateRole('USER');
	var strRoleManager = testdata.CreateRole('MANAGER');
	var strRoleAdmin = testdata.CreateRole('ADMIN');
	var strUserAlice = testdata.CreateUser('ALICE',['USER']);
	var strUserBob = testdata.CreateUser('BOB',['USER','MANAGER']);
	var strUserCharlie = testdata.CreateUser('CHARLIE',['USER','MANAGER','ADMIN']);
	

	Log('An empty user has no roles');
	TestCase_GetRolesFromUser('',[]);
	
	Log('User Alice has the USER role');
	TestCase_GetRolesFromUser(strUserAlice,[strRoleUser]);

	Log('User Bob has the USER and the MANAGER role');
	TestCase_GetRolesFromUser(strUserBob,[strRoleUser,strRoleManager]);
	
	Log('User Charlie has all roles');
	TestCase_GetRolesFromUser(strUserCharlie,[strRoleUser,strRoleManager,strRoleAdmin]);	

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
