sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var bResult = true;
	
	function TestCase_RenderValue(vInput, vExpected)
	{
		Log('TestCase_RenderValue');
		Log('GetType(): '+GetType(vInput));
		Log('Input: '+RenderValue(vInput));
		Log('Expected: '+GetStringValue(vExpected));
		var vResult = RenderValue(vInput);
		Log('Result  : '+vResult);
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	function TestCase_RenderValueHideEmpty(vInput, vExpected)
	{
		Log('TestCase_RenderValueHideEmpty');
		Log('GetType(): '+GetType(vInput));
		Log('Input: '+RenderValue(vInput));
		Log('Expected: '+GetStringValue(vExpected));
		var vResult = RenderValue(vInput,true);
		Log('Result  : '+vResult);
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}	
	
	TestCase_RenderValue(null,'null');
	TestCase_RenderValue('','');
	TestCase_RenderValue('Hello World','Hello World');
	TestCase_RenderValue(42,'42');
	TestCase_RenderValue([],'[]');
	TestCase_RenderValue(['Alpha'],'\n[0]: Alpha');
	TestCase_RenderValue({},'{}');
	TestCase_RenderValue(
		{
			'Hello': 'World'
		},
		'\n["Hello"]: World');
	TestCase_RenderValue(
		{
			'Level1':
				{
					'Level2': 'Test'
				}
		},
		'\n["Level1"]: '
		+'\n___["Level2"]: Test'
		);
	
	var gr = new GlideRecord('x_snc_devtools_test');
	gr.initialize();
	gr.name = 'Test';
	gr.value = 'Hello World';
	TestCase_RenderValue(gr,
		'x_snc_devtools_test:'
		+'\n["name"]: Test'
		+'\n["processed"]: '
		+'\n["sys_created_by"]: '
		+'\n["sys_created_on"]: '
		+'\n["sys_id"]: '
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 0'
		+'\n["sys_tags"]: '
		+'\n["sys_updated_by"]: '
		+'\n["sys_updated_on"]: '
		+'\n["value"]: Hello World'
		);

	
	TestCase_RenderValueHideEmpty(gr,
		'x_snc_devtools_test:'
		+'\n["name"]: Test'
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 0'
		+'\n["value"]: Hello World'
		);	
	
	var strSysId = gr.insert();
	TestCase_RenderValue(gr,
		'x_snc_devtools_test: '+strSysId+':'
		+'\n["name"]: Test'
		+'\n["processed"]: '
		+'\n["sys_created_by"]: '+gr.sys_created_by
		+'\n["sys_created_on"]: '+gr.sys_created_on
		+'\n["sys_id"]: '+strSysId
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 0'
		+'\n["sys_tags"]: '
		+'\n["sys_updated_by"]: '+gr.sys_updated_by
		+'\n["sys_updated_on"]: '+gr.sys_updated_on
		+'\n["value"]: Hello World'
		);
	
	
	TestCase_RenderValueHideEmpty({},'{}');
	TestCase_RenderValueHideEmpty(
		{
			'key1': 'Hello',
			'key2': 'World'
		},
		'\n["key1"]: Hello'
		+'\n["key2"]: World');
	TestCase_RenderValueHideEmpty(
		{
			'key1': 'Hello',
			'key2': ''
		},
		'\n["key1"]: Hello');
	
	TestCase_RenderValueHideEmpty(
		{
			'Level1':
				{
					'Level2': 'Test',
					'Empty': ''
				}
		},
		'\n["Level1"]: '
		+'\n___["Level2"]: Test'
		);	
		

	TestCase_RenderValue(
		{
			alpha: 'Alpha',
			sys_id: 'sys_id',
			beta: 'Beta',
			name: 'name',
			gamma: 'Gamma',
			short_description: 'short_description',
			delta: 'Delta',
			number: 'number'
		},''
		+'\n["alpha"]: Alpha'
		+'\n["beta"]: Beta'
		+'\n["delta"]: Delta'
		+'\n["gamma"]: Gamma'
		+'\n["name"]: name'
		+'\n["number"]: number'		
		+'\n["short_description"]: short_description'
		+'\n["sys_id"]: sys_id'
		);
	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
