sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RenderValue(vInput, bHideEmpty, bUnsortedKeys, bCompactOutput, vExpected)
	{
		test.Log('GetType(): '+GetType(vInput))
		test.TestCase(
			'Function RenderValue()',
			RenderValue,
			{
				'Input': vInput,
				'Hide Empty': bHideEmpty,
				'Unsorted Keys': bUnsortedKeys,
				'Compact Output': bCompactOutput
			},
			vExpected,
			null);		
	}
	
	function TestCase_RenderValueDefault(vInput, vExpected)
	{
		test.Log('RenderValue: Default');
		TestCase_RenderValue(vInput, false, false, false, vExpected);
	}	
	
	
	function TestCase_RenderValueHideEmpty(vInput, vExpected)
	{
		test.Log('RenderValue: Hide Empty');
		TestCase_RenderValue(vInput, true, false, false, vExpected);
	}	

	
	function TestCase_RenderValueUnsortedKeys(vInput, vExpected)
	{
		test.Log('RenderValue: Unsorted Keys');
		TestCase_RenderValue(vInput, false, true, false, vExpected);
	}	
	
	function TestCase_RenderValueCompactOutput(vInput, vExpected)
	{
		test.Log('RenderValue: Compact Output');
		TestCase_RenderValue(vInput, false, false, true, vExpected);		
	}		


	TestCase_RenderValueDefault(null,'null');
	TestCase_RenderValueDefault('','');
	TestCase_RenderValueDefault('Hello World','Hello World');
	TestCase_RenderValueDefault(42,'42');
	TestCase_RenderValueDefault([],'[]');
	TestCase_RenderValueDefault(['Alpha'],'\n[0]: Alpha');
	TestCase_RenderValueDefault({},'{}');
	TestCase_RenderValueDefault(
		{
			'Hello': 'World'
		},
		'\n["Hello"]: World');
	TestCase_RenderValueDefault(
		{
			'Level1':
				{
					'Level2': 'Test'
				}
		},
		'\n["Level1"]: '
		+'\n___["Level2"]: Test'
		);
	
	TestCase_RenderValueDefault(
		[
			{
				'Key': 'Value'
			}
		],
		'\n[0]: '
		+'\n___["Key"]: Value'
		);

	TestCase_RenderValueDefault(
		[
			[]
		],
		'\n[0]: '
		+'\n___[]'
		);	

	
	TestCase_RenderValueDefault(
		[
			{}
		],
		'\n[0]: '
		+'\n___{}'
		);	

	var gr = new GlideRecord('x_snc_devtools_test');
	gr.initialize();
	gr.name = 'Test';
	gr.value = 'Hello World';
	TestCase_RenderValueDefault(gr,
		'\nx_snc_devtools_test:'
		+'\n["name"]: Test'
		+'\n["order"]: '
		+'\n["processed"]: '
		+'\n["sys_created_by"]: '
		+'\n["sys_created_on"]: '
		+'\n["sys_id"]: '
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: '
		+'\n["sys_tags"]: '
		+'\n["sys_updated_by"]: '
		+'\n["sys_updated_on"]: '
		+'\n["value"]: Hello World'
		);
	
	TestCase_RenderValueHideEmpty(gr,
		'\nx_snc_devtools_test:'
		+'\n["name"]: Test'
		+'\n["sys_meta"]: null'
		//+'\n["sys_mod_count"]: 0'
		+'\n["value"]: Hello World'
		);	

	gr.insert();
	gr.update();
	TestCase_RenderValueHideEmpty(gr,
		'\nx_snc_devtools_test: '+gr.sys_id+':'
		+'\n["name"]: Test'
		+'\n["sys_created_by"]: '+gr.sys_created_by
		+'\n["sys_created_on"]: '+gr.sys_created_on
		+'\n["sys_id"]: '+gr.sys_id
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 1'
		+'\n["sys_updated_by"]: '+gr.sys_updated_by
		+'\n["sys_updated_on"]: '+gr.sys_updated_on	
		+'\n["value"]: Hello World'
		);		

	
	var gr = new GlideRecord('x_snc_devtools_test');
	gr.initialize();
	gr.name = 'Test';
	gr.value = 'Hello World';	
	
	// Nested GlideRecords
	TestCase_RenderValueDefault(
		{
			'record1': gr
		},
		'\n["record1"]: '
		+'\n___x_snc_devtools_test:'
		+'\n___["name"]: Test'
		+'\n___["order"]: '
		+'\n___["processed"]: '
		+'\n___["sys_created_by"]: '
		+'\n___["sys_created_on"]: '
		+'\n___["sys_id"]: '
		+'\n___["sys_meta"]: null'
		+'\n___["sys_mod_count"]: '
		+'\n___["sys_tags"]: '
		+'\n___["sys_updated_by"]: '
		+'\n___["sys_updated_on"]: '
		+'\n___["value"]: Hello World'
		);
	TestCase_RenderValueHideEmpty(
		{
			'record1': gr
		},
		'\n["record1"]: '
		+'\n___x_snc_devtools_test:'
		+'\n___["name"]: Test'
		+'\n___["sys_meta"]: null'
		//+'\n___["sys_mod_count"]: 0'
		+'\n___["value"]: Hello World'
		);		
	
	var strSysId = gr.insert();
	TestCase_RenderValueDefault(gr,
		'\nx_snc_devtools_test: '+strSysId+':'
		+'\n["name"]: Test'
		+'\n["order"]: '
		+'\n["processed"]: '
		+'\n["sys_created_by"]: '+gr.sys_created_by
		+'\n["sys_created_on"]: '+gr.sys_created_on
		+'\n["sys_id"]: '+strSysId
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 0'
		+'\n["sys_tags"]: '
		+'\n["sys_updated_by"]: '+gr.sys_updated_by
		+'\n["sys_updated_on"]: '+gr.sys_updated_on
		+'\n["value"]: Hello World'
		);
	
	
	TestCase_RenderValueHideEmpty({},'{}');
	TestCase_RenderValueHideEmpty(
		{
			'key1': 'Hello',
			'key2': 'World'
		},
		'\n["key1"]: Hello'
		+'\n["key2"]: World');
	TestCase_RenderValueHideEmpty(
		{
			'key1': 'Hello',
			'key2': ''
		},
		'\n["key1"]: Hello');
	
	TestCase_RenderValueHideEmpty(
		{
			'Level1':
				{
					'Level2': 'Test',
					'Empty': ''
				}
		},
		'\n["Level1"]: '
		+'\n___["Level2"]: Test'
		);	
		

	TestCase_RenderValueDefault(
		{
			alpha: 'Alpha',
			sys_id: '0815abcd',
			beta: 'Beta',
			name: 'Name',
			gamma: 'Gamma',
			short_description: 'Short Description',
			delta: 'Delta',
			number: 'TEST1234'
		},
		'\nTEST1234: 0815abcd: '
		+'\n["name"]: Name'
		+'\n["short_description"]: Short Description'
		+'\n["alpha"]: Alpha'
		+'\n["beta"]: Beta'
		+'\n["delta"]: Delta'
		+'\n["gamma"]: Gamma'
		+'\n["number"]: TEST1234'		
		+'\n["sys_id"]: 0815abcd'
		);

	
	// Special treatment of underscores
	
	TestCase_RenderValueDefault(
		{
			alpha: 'Alpha',
			beta: 'Beta',
			name: 'Name',
			gamma: 'Gamma',
			_underscore: 'Should be at the end',
			short_description: 'Short Description',
			delta: 'Delta',
			number: 'TEST1234'
		},
		'\n["alpha"]: Alpha'
		+'\n["beta"]: Beta'
		+'\n["delta"]: Delta'
		+'\n["gamma"]: Gamma'
		+'\n["name"]: Name'
		+'\n["number"]: TEST1234'		
		+'\n["short_description"]: Short Description'
		+'\n["_underscore"]: Should be at the end'
		);	
	
	TestCase_RenderValueDefault(
		{
			alpha: 'Alpha',
			sys_id: '0815abcd',
			beta: 'Beta',
			name: 'Name',
			_underscore: 'Should be at the end',
			gamma: 'Gamma',
			short_description: 'Short Description',
			delta: 'Delta',
			number: 'TEST1234'
		},
		'\nTEST1234: 0815abcd: '
		+'\n["name"]: Name'
		+'\n["short_description"]: Short Description'
		+'\n["alpha"]: Alpha'
		+'\n["beta"]: Beta'
		+'\n["delta"]: Delta'
		+'\n["gamma"]: Gamma'
		+'\n["number"]: TEST1234'		
		+'\n["sys_id"]: 0815abcd'
		+'\n["_underscore"]: Should be at the end'
		);	


/*	
	function TestFunction() { return "Hello World"; };
	TestCase_RenderValue(
		TestFunction,
		''
		+'\nfunction TestFunction() {'
		+'\n[source code not available]'
		+'\n}'
		+'\n'
		);	
*/	
	
	
	
	TestCase_RenderValueUnsortedKeys(
		{
			alpha: 'Alpha',
			sys_id: '0815abcd',
			beta: 'Beta',
			name: 'Name',
			gamma: 'Gamma',
			short_description: 'Short Description',
			delta: 'Delta',
			number: 'TEST1234'
		},
		'\nTEST1234: 0815abcd: '
		+'\n["alpha"]: Alpha'
		+'\n["sys_id"]: 0815abcd'
		+'\n["beta"]: Beta'
		+'\n["name"]: Name'
		+'\n["gamma"]: Gamma'
		+'\n["short_description"]: Short Description'
		+'\n["delta"]: Delta'
		+'\n["number"]: TEST1234'		
		);
	
	// Compact Output
	
	var gr = new GlideRecord('x_snc_devtools_test');
	gr.initialize();
	gr.name = 'Test';
	gr.value = 'Hello World';
	var strSysId = gr.insert();
	TestCase_RenderValueCompactOutput(gr,
		'\nx_snc_devtools_test: '+strSysId+': "Test"'
		);
	

	var gr = new GlideRecord('kb_knowledge');
	gr.initialize();
	gr.short_description = 'Test';
	gr.text = 'Hello World';
	var strSysId = gr.insert();
	var strNumber = gr.number;
	TestCase_RenderValueCompactOutput(gr,
		'\nkb_knowledge: '+strSysId+': '+strNumber+': "Test"'
		);	


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
