sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ParseXml(strXml, vExpected)
	{
		function TestCase_ParseXml_CheckResult(vInput,vExpected,vResult)
		{
			return IsEqual(vExpected,vResult);
		}

		test.TestCase(
			'Function ParseXml()',
			ParseXml,
			{
				'Xml': strXml
			},
			vExpected,
			TestCase_ParseXml_CheckResult);
	}

	TestCase_ParseXml(null,false);
	TestCase_ParseXml('',false);
	TestCase_ParseXml(' ',false);
	TestCase_ParseXml('<>',false);
	TestCase_ParseXml('hello world',false);
	//TestCase_ParseXml('<tag>&#d;</tag>',false);  // This is crashing the whole script instead of throwing a catchable exception

	TestCase_ParseXml(
		'<hello>world</hello>',
		{
			hello: 'world'
		});
	
	
	TestCase_ParseXml(
		'<hello>world</hello>',
		{
			hello: 'world'
		});

	TestCase_ParseXml(
		'<hello><world></world></hello>',
		{
			hello: {
				world: '',
			}
		});
	
	TestCase_ParseXml(
		'<outer><inner>core</inner></outer>',
		{
			outer: {
				inner: 'core',
			}
		});

	TestCase_ParseXml(
		'<outer><inner>Line 1\nLine 2</inner></outer>',
		{
			outer: {
				inner: 'Line 1\nLine 2',
			}
		});	

	TestCase_ParseXml(
		'<outer><inner>Line 1&#xD;Line 2</inner></outer>',
		{
			outer: {
				inner: 'Line 1Line 2',
			}
		});		

	TestCase_ParseXml(
		'<outer><inner>Line 1&#13;Line 2</inner></outer>',
		{
			outer: {
				inner: 'Line 1Line 2',
			}
		});			

	TestCase_ParseXml(
		'<outer><inner>Line 1&#xA;Line 2</inner></outer>',
		{
			outer: {
				inner: 'Line 1\nLine 2',
			}
		});			
	
	TestCase_ParseXml(
		'<outer><inner>Line 1&#10;Line 2</inner></outer>',
		{
			outer: {
				inner: 'Line 1\nLine 2',
			}
		});		
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
