sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_ParseTime(strTime,vExpected)
	{
		function TestCase_ParseTime_CheckResult(vInput,vExpected,vResult)
		{
			test.Log('RenderDateTime(vResult): '+RenderDateTime(vResult));
			return (vExpected == RenderTime(vResult));
		}
		
		test.TestCase(
			'Function ParseTime()',
			ParseTime,
			{
				'strTime': strTime
			},
			vExpected,
			TestCase_ParseTime_CheckResult);
	}
		
	TestCase_ParseTime(null,false);
	TestCase_ParseTime('',false);
	TestCase_ParseTime('abc',false);
	TestCase_ParseTime('25:00',false);

	TestCase_ParseTime('11:55:00','11:55:00');
	TestCase_ParseTime('11:55:0','11:55:00');
	TestCase_ParseTime('11:55:','11:55:00');
	TestCase_ParseTime('11:55','11:55:00');
	TestCase_ParseTime('11:5','11:05:00');
	TestCase_ParseTime('11:','11:00:00');
	TestCase_ParseTime('11','11:00:00');
	TestCase_ParseTime('1','01:00:00');

	TestCase_ParseTime('01','01:00:00');
	TestCase_ParseTime('01:0','01:00:00');
	TestCase_ParseTime('01:01','01:01:00');
	TestCase_ParseTime('01:01:','01:01:00');
	TestCase_ParseTime('01:01:1','01:01:01');
	TestCase_ParseTime('01:01:01','01:01:01');



	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
