sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_StringFormat(strInput, vParam1, vParam2, vParam3, vExpected)
	{
		test.TestCase(
			'Function StringFormat()',
			StringFormat,
			{
				'Input': strInput,
				'Param1': vParam1,
				'Param2': vParam2,
				'Param3': vParam3,
			},
			vExpected,
			null);
	}


	TestCase_StringFormat(null,null,null,null,'');
	TestCase_StringFormat('',null,null,null,'');
	TestCase_StringFormat('','',null,null,'');

	TestCase_StringFormat('Hello World','John',null,null,'Hello World');
	TestCase_StringFormat('Hello {0}','John',null,null,'Hello John');
	TestCase_StringFormat('Add {0} and {1} to get {2}.','me','you','a team','Add me and you to get a team.');
	TestCase_StringFormat('Hello {0}, this is a {2} {1}.','John','test','great','Hello John, this is a great test.');
	
	
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = '_ATF_DEVTOOLS_StringFormat_';
	grTest.value = 'Hello';
	grTest.insert();
	var strRecordSummary = RenderRecordSummary(grTest);
	TestCase_StringFormat('GetRecord("{0}", "{1}") returns: {2}.',
		grTest.getRecordClassName(),
		grTest.sys_id,
		strRecordSummary,
		'GetRecord("x_snc_devtools_test", "'+grTest.sys_id+'") returns: '+strRecordSummary+'.');
	

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
