sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);

	function TestCase_GetRoleNamesFromUser(strUserSysId, vExpected)
	{
		test.TestCase(
			'GetRoleNamesFromUser()',
			GetRoleNamesFromUser,
			{
				'User SysId': strUserSysId 
			},
			vExpected,
			TestCase_GetRoleNamesFromUser_CheckResult);
	}

	function TestCase_GetRoleNamesFromUser_CheckResult(vInput,vExpected,vResult)
	{
		return (IsEqual(vResult,vExpected,true));
	}
	
	
	test.SetResult(true);

	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	
	var strRoleUser = testdata.CreateRole('USER');
	var strRoleManager = testdata.CreateRole('MANAGER');
	var strRoleAdmin = testdata.CreateRole('ADMIN');
	var strUserAlice = testdata.CreateUser('ALICE',['USER']);
	var strUserBob = testdata.CreateUser('BOB',['USER','MANAGER']);
	var strUserCharlie = testdata.CreateUser('CHARLIE',['USER','MANAGER','ADMIN']);
	

	test.Log('An empty user has no roles');
	TestCase_GetRoleNamesFromUser('',[]);
	
	test.Log('User Alice has the USER role');
	TestCase_GetRoleNamesFromUser(strUserAlice,[
		testdata.GetPrefix()+'USER']);

	test.Log('User Bob has the USER and the MANAGER role');
	TestCase_GetRoleNamesFromUser(strUserBob,[
		testdata.GetPrefix()+'USER',
		testdata.GetPrefix()+'MANAGER']);
	
	test.Log('User Charlie has all roles');
	TestCase_GetRoleNamesFromUser(strUserCharlie,[
		testdata.GetPrefix()+'USER',
		testdata.GetPrefix()+'MANAGER',
		testdata.GetPrefix()+'ADMIN']);	

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
