sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_GetPrefix(testdata, vExpected)
	{
		function GetPrefix(testdata)
		{
			return testdata.GetPrefix();
		}
		test.TestCase(
			'GetPrefix',
			GetPrefix,
			{
				testdata: testdata
			},
			vExpected,
			null);
	}


	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	
	TestCase_GetPrefix(testdata,'_ATF_DEVTOOLS_');
	
	var strSysIdTestItem1 = testdata.CreateTestItem('ITEM1','VALUE1');
	var strSysIdTestItem2 = testdata.CreateTestItem('ITEM2','VALUE2');
	
	test.TestCase_GetRecord('x_snc_devtools_test',strSysIdTestItem1,false,true);
	test.TestCase_GetRecord('x_snc_devtools_test',strSysIdTestItem2,false,true);
	test.TestCase_GetRecord('x_snc_devtools_test','',false,false);
	
	test.TestCase_GetRecord('x_snc_devtools_test',testdata.GetTestItem('ITEM1'),false,true);
	test.TestCase_GetRecord('x_snc_devtools_test',testdata.GetTestItem('ITEM2'),false,true);
	test.TestCase_GetRecord('x_snc_devtools_test',testdata.GetTestItem('ITEM_DOES_NOT_EXIST'),false,false);
	
	test.TestCase_RecordGetValue('x_snc_devtools_test',strSysIdTestItem1,'value','VALUE1');
	test.TestCase_RecordGetValue('x_snc_devtools_test',strSysIdTestItem2,'value','VALUE2');
	
	
	test.Log(RenderValue(testdata.GetAllData()));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
