sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordSetValue(
		strInputTable,
		strInputSysId,
		strInputField,
		vInputValue,
		bInputGhost,
		vExpected)
	{
		function TestCase_RecordSetValue_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function RecordSetValue()',
			RecordSetValue,
			{
				'Table': strInputTable,
				'SysId': strInputSysId,
				'Field': strInputField,
				'Value': vInputValue,
				'Ghost': bInputGhost
			},
			vExpected,
			TestCase_RecordSetValue_CheckResult);
	}

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.value = 'Hello Europe';
	var strSysId = grTest.insert();
	TestCase_RecordSetValue('x_snc_devtools_test',strSysId,'value','Hello World',false,true);
	
	TestCase_RecordSetValue('thistabledoesnotexist','invalidsys_id','value','Hello World',false,false);
	TestCase_RecordSetValue('x_snc_devtools_test','invalidsysid','value','Hello World',false,false);
	TestCase_RecordSetValue('x_snc_devtools_test',strSysId,'thisvaluedoesnotexist','Hello World',false,false);

	
	var grIncident = new GlideRecord('incident');
	grIncident.initialize();
	grIncident.short_description = 'Hello Earth';
	var strSysId = grIncident.insert();
	TestCase_RecordSetValue('incident',strSysId,'short_description','Hello Universe',true,true);
	

	var grNumber = new GlideRecord('sys_number');
	grNumber.addQuery('category','=','incident');
	grNumber.query();
	if (grNumber.next())
	{
		TestCase_RecordSetValue('sys_number',grNumber.sys_id,'prefix','INCIDENT',true,true);
		TestCase_RecordSetValue('sys_number',grNumber.sys_id,'doesnotexist','INCIDENT',true,false);
	}
	TestCase_RecordSetValue('sys_number','doesnotexist','prefix','SOMEPREFIX',true,false);

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);

