sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var bResult = true;
	
	function TestCase_IsEqual(vInput1, vInput2, bIgnoreOrder, vExpected)
	{
		Log('TestCase_IsEqual');
		Log('Input1: '+RenderValue(vInput1));
		Log('Input2: '+RenderValue(vInput2));
		Log('bIgnoreOrder: '+RenderValue(bIgnoreOrder));
		Log('Expected: '+RenderValue(vExpected));
		var vResult = IsEqual(vInput1,vInput2,bIgnoreOrder);
		Log('Result  : '+vResult);
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	TestCase_IsEqual(null,false,false,false);
	
	TestCase_IsEqual(true,true,false,true);
	TestCase_IsEqual(true,false,false,false);

	TestCase_IsEqual(42,42,false,true);
	TestCase_IsEqual(43,42,false,false);
	
	TestCase_IsEqual('','',false,true);
	TestCase_IsEqual('Alpha','Alpha',false,true);
	TestCase_IsEqual('Alpha','Beta',false,false);

	TestCase_IsEqual([],[],false,true);
	TestCase_IsEqual(['Alpha'],['Alpha'],false,true);
	TestCase_IsEqual(['Alpha'],['Beta'],false,false);
	TestCase_IsEqual(['Alpha','Beta'],['Alpha','Beta'],false,true);
	TestCase_IsEqual(['Alpha'],['Alpha','Beta'],false,false);
	TestCase_IsEqual(['Alpha','Beta'],['Alpha'],false,false);

	TestCase_IsEqual(['Alpha','Beta'],['Beta','Alpha'],true,true);

	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
