sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetRecordsFromUpdateXml(vUpdateXml, vExpected)
	{
		function TestCase_GetRecordsFromUpdateXml_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return vExpected == vResult;
			}
			if (vExpected < 0)
			{
				return vResult.length >= -vExpected;
			}
			return (vResult.length == vExpected);
		}

		test.TestCase(
			'Function GetRecordsFromUpdateXml()',
			GetRecordsFromUpdateXml,
			{
				'vUpdateXml': vUpdateXml
			},
			vExpected,
			TestCase_GetRecordsFromUpdateXml_CheckResult);
	}

	TestCase_GetRecordsFromUpdateXml(null,false);

	var grUpdateSet = new GlideRecord('sys_update_set');
	grUpdateSet.addQuery('sys_created_by','=','system');
	grUpdateSet.query();
	var aExcludedUpdateSetSysIds = [];
	while (grUpdateSet.next())
	{
		aExcludedUpdateSetSysIds.push(grUpdateSet.getValue('sys_id'));
	}


	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.setLimit(1);
	grUpdateXml.addQuery('action','!=','DELETE');
	grUpdateXml.addQuery('update_set','NOT IN',aExcludedUpdateSetSysIds);
	grUpdateXml.query();
	if (grUpdateXml.next())
	{
		test.Log("'action','!=','DELETE'");
		TestCase_GetRecordsFromUpdateXml(grUpdateXml,-1);
	}

	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.setLimit(1);
	grUpdateXml.addQuery('action','!=','DELETE');
	grUpdateXml.addQuery('update_set','NOT IN',aExcludedUpdateSetSysIds);	
	grUpdateXml.addQuery('name','STARTSWITH','sys_documentation_');
	grUpdateXml.query();
	if (grUpdateXml.next())
	{
		test.Log("name','STARTSWITH','sys_documentation_'");
		TestCase_GetRecordsFromUpdateXml(grUpdateXml,-1);
	}

	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.setLimit(1);
	grUpdateXml.addQuery('action','!=','DELETE');
	grUpdateXml.addQuery('update_set','NOT IN',aExcludedUpdateSetSysIds);
	grUpdateXml.addQuery('name','STARTSWITH','sys_documentation_');
	grUpdateXml.query();
	if (grUpdateXml.next())
	{
		test.Log("name','STARTSWITH','sys_documentation_'");
		TestCase_GetRecordsFromUpdateXml(grUpdateXml,-1);
	}

	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.setLimit(1);
	grUpdateXml.addQuery('name','STARTSWITH','sys_choice_');
	grUpdateXml.addQuery('action','!=','DELETE');
	grUpdateXml.addQuery('update_set','NOT IN',aExcludedUpdateSetSysIds);
	grUpdateXml.query();
	if (grUpdateXml.next())
	{
		test.Log("name','STARTSWITH','sys_choice_'");
		TestCase_GetRecordsFromUpdateXml(grUpdateXml,-1);
	}	

	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.setLimit(1);
	grUpdateXml.addQuery('name','STARTSWITH','sys_script_');
	grUpdateXml.addQuery('action','!=','DELETE');
	grUpdateXml.addQuery('update_set','NOT IN',aExcludedUpdateSetSysIds);
	grUpdateXml.query();
	if (grUpdateXml.next())
	{
		test.Log("name','STARTSWITH','sys_script_'");
		TestCase_GetRecordsFromUpdateXml(grUpdateXml,-1);
	}	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
