sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);


	function TestCase_RecordTransporterRecordPack(vPackage, vRecordOrTable, strSysId, vExpected)
	{
		function TestCase_RecordTransporterRecordPack_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == true)
			{
				return IsValidSysId(vResult);
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function RecordTransporterRecordPack()',
			RecordTransporterRecordPack,
			{
				'Package': vPackage,
				'Record or Table': vRecordOrTable,
				'Sys ID': strSysId
			},
			vExpected,
			TestCase_RecordTransporterRecordPack_CheckResult);
	}

	TestCase_RecordTransporterRecordPack(null,null,null,false);
	TestCase_RecordTransporterRecordPack('x_your_scope.does_not_exist',null,null,false);

	var grPackage = new GlideRecord('x_snc_devtools_transporter_package');
	grPackage.initialize();
	grPackage.name = '_ATF_DEVTOOLS_Test1';
	grPackage.id_suffix = 'atf_test1';
	grApp = GetAppRecord('x_snc_devtools');
	grPackage.sys_scope = grApp.sys_id;
	var strPackageSysId = grPackage.insert();
	test.Log(grPackage);

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Test1';
	grTest.value = 'Hello World';
	var strTestSysId = grTest.insert();
	test.Log(grTest);

	TestCase_RecordTransporterRecordPack('x_snc_devtools.atf_test1','x_snc_devtools_test',strTestSysId,true);
	TestCase_RecordTransporterRecordPack('x_snc_devtools.atf_test1',grTest,null,true);


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
