sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_InstanceScanCheckOnRecord(strCheckSysId, strTable, strRecordSysId, vExpected)
	{
		function TestCase_InstanceScanCheckOnRecord_CheckResult(vInput,vExpected,vResult)
		{
			var bFinding = false;
			var aFindings = GetKeys(GetValue(vResult,'findings'));
			for (var nFinding = 0; nFinding < aFindings.length; nFinding++)
			{
				var strFinding = aFindings[nFinding];
				var finding = GetValue(vResult,'findings',strFinding);
				if (finding.check == strCheckSysId)
				{
					bFinding = true;
				}
			}
			return (vExpected == bFinding);
		}	
		
		test.TestCase(
			'Function InstanceScanCheckOnRecord()',
			InstanceScanCheckOnRecord,
			{
				'CheckSysId' : strCheckSysId,
				'Table'      : strTable,
				'RecordSysId': strRecordSysId,
				'Detailed'   : false
			},
			vExpected,
			TestCase_InstanceScanCheckOnRecord_CheckResult);
	}

	test.Log('Using an OOTB check against a sys_app');
	
	var strCheckSysId = '8be6ae7f8742101001c8de05b7cb0b55'; // OOTB check "Unintentional Cross Scope privileges"
	var grApp = GetAppRecord('x_snc_devtools');
	TestCase_InstanceScanCheckOnRecord('','sys_app',grApp.sys_id,false);
	TestCase_InstanceScanCheckOnRecord('','sys_app','12345678901234567890123456789012',false);
	TestCase_InstanceScanCheckOnRecord(strCheckSysId,'sys_app','12345678901234567890123456789012',false);
	TestCase_InstanceScanCheckOnRecord(strCheckSysId,'sys_app',grApp.sys_id,true);
	
	test.Log('Using a mock check against a sys_app');
	
	var grCheck = new GlideRecord('scan_table_check');
	grCheck.initialize();
	grCheck.name = 'Test1';
	grCheck.short_description = 'Test1';
	grCheck.category = 'performance';
	grCheck.table = 'sys_app';
	grCheck.conditions = 'active=true^EQ';
	grCheck.priority = '1';
	grCheck.sys_scope = 'global';
	var strCheckSysId = GlideRecordInsert(grCheck);
	//test.Log(RenderValue(grCheck));
	TestCase_InstanceScanCheckOnRecord(strCheckSysId,'sys_app',grApp.sys_id,true);
	
	test.Log('Using a mock check against a sys_script_include');
	var grCheck = new GlideRecord('scan_table_check');
	grCheck.initialize();
	grCheck.name = 'Test2';
	grCheck.short_description = 'Test2';
	grCheck.category = 'performance';
	grCheck.table = 'sys_script_include';
	grCheck.conditions = 'active=true^EQ';
	grCheck.priority = '1';
	grCheck.sys_scope = 'global';
	var strCheckSysId = GlideRecordInsert(grCheck);
	var strScriptInclude = 'd4077b701b6755105b76777d8b4bcb65'; // Script Include x_snc_devtools.InstanceScanCheckOnRecord
	TestCase_InstanceScanCheckOnRecord(strCheckSysId,'sys_script_include',strScriptInclude,true);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
