sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_Merge(vInput1, vInput2, vExpected)
	{
		Log('TestCase_Merge');
		Log('Input1: '+RenderValue(vInput1));
		Log('Input2: '+RenderValue(vInput2));
		Log('Expected: '+RenderValue(vExpected));
		var vResult = Merge(vInput1,vInput2);
		Log('Result  : '+RenderValue(vResult));
		if (IsEqual(vResult,vExpected) == false)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	TestCase_Merge({},{},{});
	TestCase_Merge(null,{},false);
	TestCase_Merge({},null,false);
	TestCase_Merge(
		{
			firstname:'John'
		},
		{
			lastname:'Doe'
		},
		{
			firstname:'John',
			lastname:'Doe'
		});
	TestCase_Merge(
		{
			firstname:'John'
		},
		{
			firstname:'Jane',
			lastname:'Doe'
		},
		{
			firstname:'Jane',
			lastname:'Doe'
		});
	TestCase_Merge(
		{
			firstname:'John',
			kids:
			{
				first: 'Alice'
			}
		},
		{
			firstname:'Jane',
			lastname:'Doe',
			kids:
			{
				second: 'Bobby'
			}
		},
		{
			firstname:'Jane',
			lastname:'Doe',
			kids:
			{
				first: 'Alice',
				second: 'Bobby'
			}
		});
	
	
		TestCase_Merge(
		{
		},
		{
			level1:
			{
				level2:
				{
					value: 'Alpha'
				}
			}
		},
		{
			level1:
			{
				level2:
				{
					value: 'Alpha'
				}
			}
		});
		
