sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_SetValue(vValue, vObject, vKey1, vKey2, vKey3, vKey4, vKey5, vKey6, vExpected)
	{
		test.TestCase(
			'Function SetValue()',
			SetValue,
			{
				'Value': vValue,
				'Object': vObject,
				'Key1': vKey1,
				'Key2': vKey2,
				'Key3': vKey3,
				'Key4': vKey4,
				'Key5': vKey5,
				'Key6': vKey6,
			},
			vExpected,
			TestCase_SetValue_CheckResult);

		function TestCase_SetValue_CheckResult(vInput,vExpected,vResult)
		{
			return IsEqual(vExpected,vResult);
		}
	}

	TestCase_SetValue(
		'Value1',
		{},
		'Key1',
		null,
		null,
		null,
		null,
		null,
		{
			Key1: 'Value1'
		});

	TestCase_SetValue(
		'Value11',
		{},
		'Key1',
		'Key11',
		null,
		null,
		null,
		null,
		{
			Key1: {
				Key11: 'Value11'
			}
		});
	
	TestCase_SetValue(
		'Value111',
		{},
		'Key1',
		'Key11',
		'Key111',
		null,
		null,
		null,
		{
			Key1: {
				Key11: {
					Key111: 'Value111'
					}
				}
		});	
	
	
	TestCase_SetValue(
		'Value112',
		{
			Key1: {
				Key11: {
					Key111: 'Value111'
					}
				}
		},
		'Key1',
		'Key11',
		'Key112',
		null,
		null,
		null,
		{
			Key1: {
				Key11: {
					Key111: 'Value111',
					Key112: 'Value112'
					}
				}
		});		
	
	TestCase_SetValue(
		'Value0',
		[],
		0,
		null,
		null,
		null,
		null,
