		1,
		null,
		null,
		null,
		null,
		null,
		[
			null,
			'Value1'
		]);		
	
	TestCase_SetValue(
		'Value01',
		[],
		0,
		1,
		null,
		null,
		null,
		null,
		[
			[null,'Value01']
		]);			
	
	TestCase_SetValue(
		'Value11',
