sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RunScriptInScope(strScope, strScript, vParam1, vParam2, vExpected)
	{
		test.TestCase(
			'Function RunScriptInScope()',
			RunScriptInScope,
			{
				'Scope': strScope,
				'Script': strScript,
				'param1': vParam1,
				'param2': vParam2
			},
			vExpected,
			null);
	}


	var strScript = "";
	strScript += "result = 'Hello '+param1+' from scope '+gs.getCurrentScopeName();"; 
	
	TestCase_RunScriptInScope('d689f6901bd38450a89720a8ec4bcbfd',strScript,'John',null,'Hello John from scope x_snc_devtools'); // Scope x_snc_devtools
	TestCase_RunScriptInScope('global',strScript,'Jane',null,'Hello Jane from scope rhino.global');
	TestCase_RunScriptInScope('ebe42450739b13005754660c4cf6a7b1',strScript,'John',null,'Hello John from scope sn_incident_atf'); // Scope sn_incident_atf

	
	
	var strScript = "";
	strScript += "var gi = new GlideImpersonate();";   // Do something that would provoke an error in a scoped application
	strScript += "result = 'Goodbye '+param1+' from scope '+gs.getCurrentScopeName();";
	
	TestCase_RunScriptInScope('global',strScript,'John',null,'Goodbye John from scope rhino.global');
	TestCase_RunScriptInScope('global',strScript,'Jane',null,'Goodbye Jane from scope rhino.global');	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
