sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_GetRelatedRecordsTree(grRecord,bApplicationFilesOnly,nMaxLevel,vExpected)
	{
		function TestCase_GetRelatedRecordsTree_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			var aRelatedRecords = GetArrayValue(GetValue(vResult,'_related_records'));
			return (aRelatedRecords.length == vExpected); 
		}
		
		test.TestCase(
			'Function GetRelatedRecordsTree()',
			GetRelatedRecordsTree,
			{
				'grRecord': grRecord,
				'bApplicationFilesOnly': bApplicationFilesOnly,
				'nMaxLevel': nMaxLevel
			},
			vExpected,
			TestCase_GetRelatedRecordsTree_CheckResult);
	}
		
	TestCase_GetRelatedRecordsTree(null,true,0,false);

	var grScriptInclude = GetRecord('sys_script_include','71c1d58b3b34f69062bf612c95e45adb');
	TestCase_GetRelatedRecordsTree(grScriptInclude,true,0,0);

	var grUiPolicy = GetRecord('sys_ui_policy','ac30af26dba34290f0f1d03cd3961915');
	TestCase_GetRelatedRecordsTree(grUiPolicy,true,0,2);

	var grDbObject = GetTableRecord('x_snc_devtools_test');
	TestCase_GetRelatedRecordsTree(grDbObject,true,0,11);

	var grIncident = new GlideRecord('incident');
	grIncident.initialize();
	grIncident.short_description = 'TEST incident';
	var strIncidentSysId = GlideRecordInsert(grIncident);

	var grIncidentTask = new GlideRecord('incident_task');
	grIncidentTask.initialize();
	grIncidentTask.short_description = 'TEST incident task';
	grIncidentTask.incident = strIncidentSysId;
	var strIncidentTaskSysId = GlideRecordInsert(grIncidentTask);

	TestCase_GetRelatedRecordsTree(grIncident,false,0,1);
	TestCase_GetRelatedRecordsTree(grIncident,true,0,0);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
