sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);



	function TestCase_GetTableFromSysId(strSysId, vExpected)
	{
		test.TestCase(
			'Function GetTableFromSysId()',
			GetTableFromSysId,
			{
				'SysId': strSysId 
			},
			vExpected,
			null);
	}


	// Invalid parameters
	TestCase_GetTableFromSysId(null,false);
	TestCase_GetTableFromSysId('',false);
	
	// Special case: 'global'
	TestCase_GetTableFromSysId('global','sys_dictionary');
	
	// Contained in sys_metadata
	TestCase_GetTableFromSysId('fc26194edb073c504647dad1f3961930','sys_script_include'); 

	// Contained in sys_package
	TestCase_GetTableFromSysId('d689f6901bd38450a89720a8ec4bcbfd','sys_app'); 

	
	// Tables derived from task
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	var strIncidentSysId = testdata.CreateIncident('TEST1');
	TestCase_GetTableFromSysId(strIncidentSysId,'incident');
	
	// Records in cmdb_ci
	TestCase_GetTableFromSysId('0e7a06157f10310016181ccebefa91ce','cmdb_ci_service_group');
	
	// Users
	var grUser = new GlideRecord('sys_user');
	grUser.setLimit(1);
	grUser.query();
	if (grUser.next())
	{
		TestCase_GetTableFromSysId(GetStringValue(grUser.sys_id),GetStringValue(grUser.sys_class_name));
	}
	
	// Worst case, we need to search all tables
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	var strTestItemSysId = testdata.CreateTestItem('TEST1');
	TestCase_GetTableFromSysId(strTestItemSysId,'x_snc_devtools_test');
	
	// A non-existing sys_id
	TestCase_GetTableFromSysId('12345678901234567890123456789012',false);
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
