sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetParentRecord(strTable, strRecordSysId, vExpected)
	{
		test.TestCase(
			'Function GetParentRecord()',
			GetParentRecord,
			{
				'Table': strTable,
				'RecordSysId': strRecordSysId
			},
			vExpected,
			TestCase_GetParentRecord_CheckResult);
	}

	function TestCase_GetParentRecord_CheckResult(vInput,vExpected,vResult)
	{
		if (vExpected == false)
		{
			return (vResult == false);
		}
		return (vExpected == GetStringValue(vResult.sys_id));
	}
	

	TestCase_GetParentRecord(null,null,false);
	TestCase_GetParentRecord('sys_atf_test',null,false);

	test.Log('Script include "GetParentRecord" does not have a parent record');
	TestCase_GetParentRecord('sys_script_include','9934e6141b6abc505331b8449b4bcbdd',false);

	test.Log('From this test to the DevTools test suite');
	TestCase_GetParentRecord('sys_atf_test','45381fa01b627090577f43b5bb4bcb34','4a8550391b5b8850a89720a8ec4bcbd9');

	test.Log('From this test step to this test');
	TestCase_GetParentRecord('sys_atf_step','f458dfa01b627090577f43b5bb4bcbb1','45381fa01b627090577f43b5bb4bcb34');
	
	
	test.Log('A test user has no parent record');
	var grUser = new GlideRecord('sys_user');
	grUser.initialize();
	grUser.user_name = 'test.getparentrecord';
	var strUserSysId = grUser.insert();
	TestCase_GetParentRecord('sys_user',strUserSysId,false);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
