sys_script_include.script:
function RenderBytes(nBytes)
{
	if (GetType(nBytes) != 'number' && GetType(nBytes) != 'ScopedGlideElement')
	{
		return '';
	}
	nBytes = GetIntegerValue(nBytes);
	if (nBytes < 0)
	{
		return '';
	}
	if (nBytes == 1)
	{
		return '1 Byte';
	}
	if (nBytes < 1024)
	{
		return nBytes + ' Bytes';
	}
	var nKB =  nBytes / 1024;
	if (nKB < 1024)
	{
		return nKB.toFixed(0) + ' KB';
	}
	var nMB = nKB / 1024;
	if (nMB < 1024)
	{
		return nMB.toFixed(1) + ' MB';
	}
	var nGB = nMB / 1024;
	return nGB.toFixed(2) + ' GB';
}
