sys_script_include.script:
function IsAclAvailableForUiPage(vUiPage)
{
	var grUiPage = GetRecord('sys_ui_page',vUiPage);
	if (IsValidRecord(vUiPage) == false)
	{
		return false;
	}

	var strName = x_snc_devtools.GetStringValue(grUiPage.name);
	if (grUiPage.sys_scope.scope != 'global')
	{
		strName = grUiPage.sys_scope.scope + '_' + strName;
	}

	var grACL = new GlideRecord('sys_security_acl');
	grACL.addActiveQuery();
	grACL.addQuery('type','=','ui_page');
	grACL.addQuery('operation','=','read');
	grACL.addQuery('name','=',strName);
	grACL.query();
	if (grACL.getRowCount() > 0)
	{
		return true;
	}

	return false;
}
