sys_script_include.script:
function IsPublicUiPage(vUiPage)
{
	var grUiPage = GetRecord('sys_ui_page',vUiPage);
	if (IsValidRecord(vUiPage) == false)
	{
		return false;
	}

	var strName = GetStringValue(grUiPage.name);
	if (grUiPage.sys_scope.scope != 'global')
	{
		strName = grUiPage.sys_scope.scope + '_' + strName;
	}

	var grPublic = new GlideRecord('sys_public');
	grPublic.addActiveQuery();
	grPublic.addQuery('page','=',strName);
	GlideRecordQuery(grPublic);
	if (grPublic.getRowCount() > 0)
	{
		return true;
	}

	return false;
}
