sys_script_include.script:
function GetRecordScopeSysId(grRecord)
{
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}

	var strRecordTable = GetStringValue(grRecord.getRecordClassName());

	if (   strRecordTable == 'sys_app'
		|| strRecordTable == 'sys_store_app'
		|| strRecordTable == 'sys_scope')
	{
		return GetStringValue(grRecord.sys_id);
	}

	if (IsDerivedFromTable(strRecordTable,'sys_metadata'))
	{
		return GetStringValue(grRecord.sys_scope);
	}

	if (strRecordTable == 'sys_metadata')
	{
		return GetStringValue(grRecord.sys_scope);
	}

	return false;
}
