sys_script_include.script:
function Base64Decompress(strEncoded)
{
	var strScript = 
		'try\n'
		+'{\n'
		+'	result = GlideCompressionUtil.expandToString(GlideStringUtil.base64DecodeAsBytes(param1));\n'
		+'}\n'
		+'catch (e)\n'
		+'{\n'
		+'    result = false;\n'
		+'}\n';

	var vDecoded = RunScriptInGlobalScope(strScript,strEncoded);
	var vParsed = ParseJson(vDecoded);
	if (IsObject(vParsed) == true || IsArray(vParsed) == true)
	{
		return vParsed;
	}
	return vDecoded;
}
