sys_script_include.script:
function IsDerivedFromTable(vTableOrRecord, strParentTable)
{
	var strTable = '';
	if (IsValidRecord(vTableOrRecord) == true)
	{
		strTable = GetStringValue(vTableOrRecord.getRecordClassName());
	}
	else
	{
		strTable = GetStringValue(vTableOrRecord);
	}

	while (strTable != false)
	{
		strTable = GetParentTable(strTable);
		if (strTable == strParentTable)
		{
			return true;
		}
	}
	return false;
}
