sys_script_include.script:
function ScriptAutoGenerateDocumentation(strScript,strDescription)
{
	strScript = GetStringValue(strScript);
	strScript = strScript.trim();
	if (strScript == '')
	{
		return false;
	}
	strDescription = GetStringValue(strDescription);

	var strAutoDocStart = '\n|\n';

	// Clear existing auto-generated documentation
	var nAutoDocStart = strDescription.indexOf(strAutoDocStart);
	if (nAutoDocStart != -1)
	{
		strDescription = strDescription.substr(0,nAutoDocStart);
	}

	// Do not add auto-generated documentation for single function scripts
	var strScriptNoComments = ScriptRemoveComments(strScript);
	if (StringCheckRegEx(strScriptNoComments,'^\\s*function\\s+') == true)
	{
		return strDescription;
	}

	// Do not add auto-generated documentation if no functions are defined
	var aFunctionNames = ScriptGetFunctionNames(strScript);
	if (aFunctionNames == false)
	{
		return strDescription;
	}
	if (aFunctionNames.length == 0)
	{
		return strDescription;
	}

	strDescription += strAutoDocStart;
	strDescription += 'Methods:\n';
	for (var nFunction = 0; nFunction < aFunctionNames.length; nFunction++)
	{
		if (nFunction > 0)
		{
			strDescription += '\n';	
		}
		strDescription += '* '+aFunctionNames[nFunction]+'()';
	}

	return strDescription;

}
