sys_script_include.script:
function IsPrimaryRequestUrl(strUrl)
{
	strUrl = GetStringValue(strUrl);
	if (IsValidUrl(strUrl) == false)
	{
		return false;
	}
	if (strUrl == GetInstanceURL())
	{
		return false;
	}
	if (strUrl.length > 500)
	{
		return false;
	}
	var aPatterns = [
		'navpage.do',
		'angular.do',
		'xmlhttp.do',
		'page_timing',
		'get?',
		'/api/',
		'polarisberg_theme_variables.do?',
		'/ds?',
		'$uxapp.do',
		'sn_devstudio_/v1/',
		'get_claimed_files',
		'/slushbucket.do?',
		'/popup.do?',
		'?isFormPage=true',
		'$createnow_ambclient_iframe.do',
		'$uxappimmutables.do?',
		'/amb/',
		'x_snc_devtools_evaluator_log',
		'/change_current_app.do?',
		'$studio.do?'
	];
	for (var nPattern = 0; nPattern < aPatterns.length; nPattern++)
	{
		if (strUrl.indexOf(aPatterns[nPattern]) != -1)
		{
			return false;
		}
	}
	return true;
}
