sys_script_include.script:
function AppBuilderAddSyntaxEditorMacro(strAppSysId,strName,strText,strComments)
{
	strAppSysId = GetStringValue(strAppSysId);
	if (IsValidSysId(strAppSysId) == false)
	{
		return false;
	}
	strName = GetStringValue(strName);
	if (strName == '')
	{
		return false;
	}
	strText = GetStringValue(strText);
	if (strText == '')
	{
		return false;
	}	
	return RecordQueryOrInsert(
		'syntax_editor_macro',
		{
			name: strName,
			sys_scope: strAppSysId
		},
		{
			comments: strComments,
			text: strText
		},
		false);
}
