sys_script_include.script:
function AppBuilderAclInsertOrUpdateForUiPage(vUiPage,strDecisionType,strDescription,aRoles,strScript)
{
	var grUiPage = GetRecord('sys_ui_page',vUiPage);
	if (IsValidRecord(grUiPage) == false)
	{
		return false;
	}
	var strScopePrefix = '';
	if (grUiPage.sys_scope.scope != 'global')
	{
		strScopePrefix = GetStringValue(grUiPage.sys_scope.scope) + '_';
	}
	strName = strScopePrefix + grUiPage.name;
	strDecisionType = GetStringValue(strDecisionType);
	if (strDecisionType == '')
	{
		return false;
	}
	strDescription = GetStringValue(strDescription);
	aRoles = GetArrayValue(aRoles);
	strScript = GetStringValue(strScript);

	var strAclSysId = AppBuilderAclInsertOrUpdate(
		grUiPage.sys_scope,
		'ui_page',
		'read',
		strName,
		strDecisionType,
		strDescription,
		aRoles,
		strScript);

	return strAclSysId;
}
