sys_script_include.script:
function HtmlRenderWarning(strText)
{
	strText = GetStringValue(strText);
	if (strText == '')
	{
		return '';
	}
	var strHtml = '';
	strHtml += '<div class="outputmsg outputmsg_warning notification notification-warning">';
		strHtml += '<div class="outputmsg_text">';
			strHtml += strText;
		strHtml += '</div>';
	strHtml += '</div>';
	return strHtml;
}
