sys_script_include.script:
function HtmlRenderTree(aNodes, nLevel, bBottomUp, callbackRenderNode, strNodes)
{
	if (IsArray(aNodes) == false)
	{
		return '';
	}

	nLevel = GetIntegerValue(nLevel);
	bBottomUp = GetBoolValue(bBottomUp);

	function RenderNodeDefault(node,nLevel)
	{
		return HtmlRenderRoundedBox(GetValue(node,'content'));
	}

	if (IsFunction(callbackRenderNode) == false)
	{
		callbackRenderNode = RenderNodeDefault;
	}

	strNodes = GetStringValue(strNodes);
	if (strNodes == '')
	{
		strNodes = '_nodes';
	}

	var strHtml = '';

	for (var nNode = 0; nNode < aNodes.length; nNode++)
	{
		var nCurrentNode = nNode;

		if (bBottomUp == true)
		{
			strHtml += HtmlRenderTree(
				GetValue(aNodes[nCurrentNode],strNodes),
				nLevel+1,
				true,
				callbackRenderNode,
				strNodes);
		}

		var nPadding = 20 * nLevel;

		strHtml += '<div style="padding-left: '+nPadding+'px;">\n';
		strHtml += callbackRenderNode(aNodes[nCurrentNode],nLevel);
		strHtml += '</div>';

		if (bBottomUp == false)
		{
			strHtml += HtmlRenderTree(
				GetValue(aNodes[nCurrentNode],strNodes),
				nLevel+1,
				false,
				callbackRenderNode,
				strNodes);
		}
	}

	return strHtml;
}
