sys_script_include.script:
function MakeUserName(vNameComponents)
{
	var aNameComponents = GetArrayValue(vNameComponents);
	var strUserName = '';
	var bTechnical = false;
	var bTest = false;
	for (var nComp = 0; nComp < aNameComponents.length; nComp++)
	{
		var strComp = aNameComponents[nComp];
		strComp = strComp.toLowerCase();
		if (nComp == 0)
		{
			if (strComp == 'test')
			{
				bTest = true;
			}
			if (strComp == 'technical')
			{
				bTechnical = true;
			}
		}
		if (bTest == true || bTechnical == true)
		{
			strComp = StringReplace(strComp,' ','.');
			strComp = StringReplace(strComp,'-','.');
		}
		else
		{
			strComp = StringReplace(strComp,' ','');
			strComp = StringReplace(strComp,'-','');
		}
		if (strComp != '')
		{
			strComp = strComp.toLowerCase();
			if (strUserName != '')
			{
				strUserName += '.';
			}
			strUserName += strComp;
		}
	}

	return strUserName;
}
