sys_script_include.script:
function RecordSetValue(strTable,strSysId,strField,vValue)
{
	if (IsValidTable(strTable) == false)
	{
		return false;
	}
	var grRecord = new GlideRecord(strTable);
	if (grRecord.isValidField(strField) == false)
	{
		return false;
	}
	if (grRecord.get(strSysId))
	{
		grRecord[strField] = vValue;
		if (grRecord.update() == strSysId)
		{
			return true;
		}
	}
	return false;
}
