sys_script_include.script:
function GetArrayValue(vValue,strDelimiter)
{
	strDelimiter = GetStringValue(strDelimiter);
	if (strDelimiter == '')
	{
		strDelimiter = ',';
	}
	if (vValue == undefined)
	{
		return [];
	}
	if (vValue === null)
	{
		return [];
	}	
	if (vValue === false)
	{
		return [];
	}
	if (IsArray(vValue))
	{
		return vValue;
	}
	var strType = GetType(vValue);
	if (strType == 'ScopedGlideElement')
	{
		vValue = GetStringValue(vValue);
		strType = 'string';
	}
	if (strType == 'string' || strType == 'String')
	{
		if (vValue == '')
		{
			return [];
		}
		return vValue.split(strDelimiter);
	}
	return [vValue];
}
