sys_script_include.script:
var InstallerAPI = Class.create();
InstallerAPI.prototype =
{
    initialize: function(strContext) 
	{
		this.m_bError = false; 
		this.m_bAborting = false;
		this.m_strContext = '';

		strContext = GetStringValue(strContext);
		if (strContext != '')
		{
			this.m_strContext = strContext + ': ';
		}
		this.m_strLog = '\n'+this.m_strContext+'Installation BEGIN\n';

		var strLegacyMode = gs.getProperty('glide.record.legacy_cross_scope_access_policy_in_script');
		if (strLegacyMode != 'true')
		{
			this.FatalError('The system property "glide.record.legacy_cross_scope_access_policy_in_script" must be set to true during DevTools installation!');
			return;
		}

		if (strContext != '')
		{
			var cache = new TransactionCacheAPI('INSTALLER');
			if (cache.GetItem(strContext) == true)
			{
				this.Log('Installation ran already. Aborting.');
				this.m_bAborting = true;
			}
			cache.SetItem(strContext,true);
		}

    },

	IsAborting: function()
	{
		return this.m_bAborting;
	},

	Log: function(vPayload)
	{
		this.m_strLog += RenderValue(vPayload) + '\n';
	},

	Error: function(strText)
	{
		this.Log('ERROR: '+strText);
		this.m_bError = true;
	},

	FatalError: function(strText)
	{
		this.Log('FATAL ERROR: ABORTING INSTALLATION: '+strText);
		this.m_bError = true;
		this.m_bAborting = true;
	},


	Finish: function()
	{
		var bResult = true;
		if (this.m_bError == false)
		{
			CacheFlushMenu();
			this.Log(this.m_strContext+'Installation COMPLETED');
			Log(this.m_strLog);
		}
		else
		{
			bResult = false;
			this.Log(this.m_strContext+'Installation FAILED');
			LogError(this.m_strLog);
		}
		return bResult;
	},

	GenericAction: function(vResult,strLogSuccess,strLogError)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}		
		if (vResult == false)
		{
			this.Error(strLogError);
		}
		else
		{
			this.Log(strLogSuccess);
			return vResult;
		}
		return vResult;
	},


	RecordInsertOrUpdate: function(strReason,strTable,query,valuesInsert,valuesUpdate)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}		

		strReason = GetStringValue(strReason);

		strTable = GetStringValue(strTable);
		if (IsValidTable(strTable) == false)
		{
			this.Error(strReason+': Table "'+strTable+'" does not exist.');
			return false;
		}
		if (IsObject(query) == false)
		{
			query = {};
		}
		if (IsObject(valuesInsert) == false)
		{
			valuesInsert = {};
		}
		if (IsObject(valuesUpdate) == false)
		{
			valuesUpdate = {};
		}
		var grRecord = new GlideRecord(strTable);
		GlideRecordAddQuery(grRecord,query);
		GlideRecordQuery(grRecord);
		if (grRecord.getRowCount() > 1)
		{
			this.Error(strReason+': There is more than one record matching the specified query. No action required.');
			return false;
		}
		if (grRecord.next())
		{
			var aKeys = GetKeys(valuesUpdate);
			if (aKeys.length == 0)
			{
				this.Log(strReason+': Record exists. No action required.');
				return true;
			}
			else
			{
				GlideRecordSetValues(grRecord,valuesUpdate);
				GlideRecordUpdate(grRecord);
				var strSysId = GetStringValue(grRecord.sys_id);
				if (IsValidSysId(strSysId))
				{
					this.Log(strReason+': Record has been updated!');
					return true;
				}
				else
				{
					this.Error(strReason+': Record could not be updated!');
					return false;
				}
			}
		}

		var values = Merge(query,valuesInsert);
		values = Merge(values,valuesUpdate);

		grRecord.initialize();
		grRecord.newRecord();
		GlideRecordSetValues(grRecord,values);
		var strSysId = GlideRecordInsert(grRecord);
		if (IsValidSysId(strSysId) == false)
		{
			this.Error(strReason+': Record could not be created!');
			return false;
		}
		this.Log(strReason+': Record has been created.');
		return true;
	},

	RecordSetValue: function(strReason,strTable,strSysId,strField,vValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		if (strTable == 'sys_dictionary'
			|| IsDerivedFromTable(strTable,'sys_dictionary'))
		{
			this.Error(strReason+': RecordSetValue cannot be used on "'+strTable+'" records. Sys Ids of "'+strTable+'" records might differ on each instance. Use InstallerAPI::DictionarySetValue() instead.');
			return false;
		}
		if (strTable == 'sys_dictionary_override')
		{
			this.Error(strReason+': RecordSetValue cannot be used on "'+strTable+'" records. Sys Ids of "'+strTable+'" records might differ on each instance.');
			return false;
		}		
		var vResult = RecordSetValue(strTable,strSysId,strField,vValue);
		if (vResult == false)
		{
			this.Error(strReason+': Failed to set record '+strTable+':'+strSysId+' value "'+strField+'" to: '+RenderValue(vValue));
			return false;
		}
		this.Log(strReason+': Set record '+strTable+': '+strSysId+' value "'+strField+'" to: '+RenderValue(vValue));
		return true;
	},

	RecordDelete: function(strReason,strTable,strSysId)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var grRecord = GetRecord(strTable,strSysId);
		if (grRecord == false)
		{
			this.Log(strReason+': Record '+strTable+' / '+strSysId+' does not exist. No action required.');
			return true;
		}

		var bResult = RecordDelete(strTable,strSysId);
		if (bResult == true)
		{
			this.Log(strReason+': Deleted record: '+strTable+' / '+strSysId);
		}
		else
		{
			this.Error(strReason+': Failed to delete record: '+strTable+' / '+strSysId);
			return false;
		}
		return true;
	},	

	DictionarySetValue: function(strReason,strTable,strElement,strField,vValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var strItem = GetStringValue(strTable);
		if (GetStringValue(strElement) != '')
		{
			strItem += '.'+strElement;
		}

		var grDict = GetDictionaryRecord(strTable,strElement);
		if (grDict == false)
		{
			this.Error(strReason+': Could not identify dictionary record for '+strItem+'.');
			return false;			
		}

		var vResult = RecordSetValue('sys_dictionary',grDict.sys_id,strField,vValue,true);
		if (vResult == false)
		{
			this.Error(strReason+': Failed to set dictionary record for '+strItem+': value "'+strField+'" to: '+RenderValue(vValue));
			return false;
		}
		this.Log(strReason+': Set dictionary record for '+strItem+': value "'+strField+'" to: '+RenderValue(vValue));
		if (strField == 'column_label')
		{
			this.Log('WARNING: Setting the label for a column may not be sufficient to modify the text displayed in the GUI. You may have to set the corresponding sys_documentation records (for multiple languages). Refer to InstallerAPI::DocumentationSetValue().');
		}
		return true;		
	},

	DictionaryOverrideSetValue: function(strReason,strTable,strElement,bOverride,strField,vValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		bOverride = GetBoolValue(bOverride);

		var strItem = GetStringValue(strTable);
		if (GetStringValue(strElement) != '')
		{
			strItem += '.'+strElement;
		}

		var grDict = GetDictionaryRecord(strTable,strElement);
		if (grDict == false)
		{
			this.Error(strReason+': Could not identify dictionary record for '+strItem+'.');
			return false;			
		}

		var grDictOverride = GetDictionaryOverrideRecord(strTable,strElement);
		if (grDictOverride == false)
		{
			var grTable = GetTableRecord(strTable);

			grDictOverride = new GlideRecord('sys_dictionary_override');
			grDictOverride.initialize();
			grDictOverride.sys_scope = grTable.sys_scope;
			grDictOverride.base_table = GetBaseTable(strTable);
			grDictOverride.name = strTable;
			grDictOverride.element = strElement;
			var strDictOverrideSysId = GlideRecordInsert(grDictOverride);
			if (IsValidSysId(strDictOverrideSysId) == false)
			{
				this.Error('Failed creating new dictionary record for item "'+strItem+'"');
				return false;
			}
		}

		var vResult = RecordSetValue('sys_dictionary_override',grDictOverride.sys_id,strField + '_override',bOverride,true);
		if (vResult == false)
		{
			this.Error(
				strReason+': Failed to set dictionary override record for '+strItem+': value "'+strField+'_override" to: '+RenderValue(bOverride));
			return false;
		}

		var vResult = RecordSetValue('sys_dictionary_override',grDictOverride.sys_id,strField,vValue,true);
		if (vResult == false)
		{
			this.Error(
				strReason+': Failed to set dictionary override record for '+strItem+': value "'+strField+'" to: '+RenderValue(vValue));
			return false;
		}
		this.Log(
			strReason+': Set dictionary override record for '+strItem+': value "'+strField+'" to: '+RenderValue(vValue) + ' and "'+strField+'_override" to '+RenderValue(bOverride));
		return true;
	},		

	DocumentationSetValue: function(strTable,strElement,strLanguage,strField,vValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var strItem = GetStringValue(strTable);
		if (GetStringValue(strElement) != '')
		{
			strItem += '.'+strElement;
		}
		strItem += ' ('+strLanguage+')';

		var values = {};
		values[strField] = vValue;
		values['sys_scope'] = '';

		var strReason = 'Set documentation record for '+strItem+': Set '+strField+' to "'+RenderValue(vValue)+'"';

		return this.RecordInsertOrUpdate(strReason,'sys_documentation',
			{
				name: strTable,
				element: strElement,
				language: strLanguage
			},
			{},
			values);
	},		

	CheckTableCrossScopeAccessibility: function(strTable)
	{
		strTable = GetStringValue(strTable);
		if (IsTableCrossScopeAccessible(strTable) == false)
		{
			this.FatalError(strReason+': Table "'+strTable+'" is not configured to be cross scope accessible.');
			return false;
		}
		this.Log('Table "'+strTable+'" is configured to be cross scope accessible. Proceeding with installation.');
		return true;
	},

	SetProperty: function(strProperty, strValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		strProperty = GetStringValue(strProperty);
		strValue = GetStringValue(strValue);

		var strOldValue = GetStringValue(gs.getProperty(strProperty));
		if (strOldValue == strValue)
		{
			this.Log('System property '+strProperty+' is already set to "'+strValue+'". No action required.');
			return true;
		}

		if (SetProperty(strProperty,strValue,true) == true)
		{
			this.Log('System property '+strProperty+' has been set to "'+strValue+'".');
			return true;
		}
		else
		{
			this.Error('Failed to set system property '+strProperty+' to "'+strValue+'".');
			return false;
		}
	},

	SetHomePage: function(strUri)
	{
		this.SetProperty('glide.login.home',strUri);
		this.SetProperty('glide.home.page',strUri);

		var strUrl = GetInstanceUrl() + 'nav_to.do?uri='+ strUri;

		this.RecordInsertOrUpdate(
			'Set home page',
			'sys_ui_page',
			{
				name: 'home',
				sys_scope: 'global'
			},
			{},
			{
				html:
					'<?xml version="1.0" encoding="utf-8" ?>\n'+
					'<j:jelly trim="false" xmlns:j="jelly:core" xmlns:g="glide" xmlns:j2="null" xmlns:g2="null">\n'+
					'\t<g2:no_escape>$[\n'+
					'\t\tx_snc_devtools.HtmlRenderRedirect(\''+strUrl+'\');\n'+
					'\t]</g2:no_escape>\n'+	
					'</j:jelly>\n'
			});		

	},

	SetTableValue: function(strTable,strField,vValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		strTable = GetStringValue(strTable);
		strField = GetStringValue(strField);

		var grTable = GetTableRecord(strTable);
		if (IsValidRecord(grTable) == false)
		{
			this.Error('Table "'+strTable+'" could not be found!');			
		}

		if (IsValidColumn('sys_db_object',strField) == false)
		{
			this.Error('Table sys_db_object does not contain field "'+strField+'"!');	
		}

		GlideRecordSetValue(grTable,strField,vValue);
		var bResult = IsValidSysId(GlideRecordUpdate(grTable,true));

		if (bResult == true)
		{
			this.Log('Table field "'+strField+'" of table "'+strTable+'" is set to "'+vValue+'".');
		}
		else
		{
			this.Error('Table field "'+strField+'" of table "'+strTable+'" is NOT set to "'+vValue+'"!');
		}
		return bResult;
	},


	SetTableAttribute: function(strTable,strAttributeName,strAttributeValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		strTable = GetStringValue(strTable);
		strAttributeName = GetStringValue(strAttributeName);
		strAttributeValue = GetStringValue(strAttributeValue);
		var bResult = TableSetAttribute(strTable,strAttributeName,strAttributeValue);
		if (bResult == true)
		{
			this.Log('Table attribute "'+strAttributeName+'" of table "'+strTable+'" is set to "'+strAttributeValue+'".');
		}
		else
		{
			this.Error('Table attribute "'+strAttributeName+'" of table "'+strTable+'" is NOT set to "'+strAttributeValue+'"!');
		}
		return bResult;

	},

	SetTableColumnAttribute: function(strTable,strColumn,strAttributeName,strAttributeValue)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		strTable = GetStringValue(strTable);
		strColumn = GetStringValue(strColumn);
		strAttributeName = GetStringValue(strAttributeName);
		strAttributeValue = GetStringValue(strAttributeValue);
		var bResult = TableColumnSetAttribute(strTable,strColumn,strAttributeName,strAttributeValue);
		if (bResult == true)
		{
			this.Log(
				'Table column attribute "'+strAttributeName+'" of column "'+strColumn+'" in table "'+strTable+'" is set to "'+strAttributeValue+'".');
		}
		else
		{
			this.Error(
				'Table column attribute "'+strAttributeName+'" of column "'+strColumn+'" in table "'+strTable+'" is NOT set to "'+strAttributeValue+'"!');
		}
		return bResult;

	},

	SetTableDefaultOrder: function(strTable,strElement,bDescending)
	{
		strTable = GetStringValue(strTable);
		strElement = GetStringValue(strElement);
		bDescending = GetBoolValue(bDescending);
		if (IsValidTable(strTable) == false)
		{
			this.Error(
				'SetTableDefaultOrder: Table "'+strTable+'" is invalid!');
			return false;
		}
		if (IsValidColumn(strTable,strElement) == false)
		{
			this.Error(
				'SetTableDefaultOrder: Table column '+strTable+'.'+strElement+' is invalid!');
			return false;
		}		
		var bResult = this.RecordInsertOrUpdate(
			'Set default ordering for table "'+strTable+'" to element "'+strElement+'"',
			'sys_user_preference',
			{
				name: strTable + '.db.order',
				user: ''
			},
			{},
			{
				value: strElement
			});
		if (bResult == false)
		{
			return false;
		}	

		var strDirection = 'Ascending';
		var strDirectionValue = '';

		if (bDescending == true)
		{
			strDirection = 'Descending';
			strDirectionValue = 'DESC';
		}

		return this.RecordInsertOrUpdate(
			'Set default ordering direction for table "'+strTable+'" to "'+strDirection+'"',
			'sys_user_preference',
			{
				name: strTable + '.db.order.direction',
				user: ''
			},
			{},
			{
				value: strDirectionValue
			});
	},

	ChoiceInsertOrUpdate: function(strReason,strTable,strElement,strLanguage,strLabel,strValue)
	{
		if (IsValidTable(strTable) == false)
		{
			this.Error(
				'ChoiceInsertOrUpdate: Table "'+strTable+'" is invalid!');
			return false;
		}
		return this.RecordInsertOrUpdate(
			StringFormat('{0}\nInsert or update a choice:\n'
				+' Table:    "{1}"\n'
				+' Element:  "{2}"\n'
				+' Language: "{3}"\n'
				+' Label:    "{4}"\n'
				+' Value:    "{5}"\n',
				strReason,strTable,strElement,strLanguage,strLabel,strValue),
			'sys_choice',
			{
				name: strTable,
				element: strElement,
				language: strLanguage,
				value: strValue
			},
			{},
			{
				label: strLabel
			});
	},


	ActivateChoice: function(strReason,strTable,strElement,strValue,bActive)
	{
		if (IsValidTable(strTable) == false)
		{
			this.Error(
				'ActivateChoice: Table "'+strTable+'" is invalid!');
			return false;
		}

		if (IsValidColumn(strTable,strElement) == false)
		{
			this.Error(
				'ActivateChoice: Element '+strTable+'.'+strElement+' is invalid!');
			return false;

		}

		var bInactive = !GetBoolValue(bActive);

		var grChoice = new GlideRecord('sys_choice');
		grChoice.addQuery('name',strTable);
		grChoice.addQuery('element',strElement);
		grChoice.addQuery('value',strValue);
		grChoice.query();
		if (grChoice.getRowCount() == 0)
		{
			this.Log(
				'ActivateChoice: Did not find any choices for '+strTable+'.'+strElement+'."'+strValue+'" to set inactive to '+RenderValue(bInactive));
			return true;
		}

		this.Log(
			'ActivateChoice: Attempting to set for all language versions of '+strTable+'.'+strElement+'."'+strValue+'" inactive to '+RenderValue(bInactive));


		while (grChoice.next())
		{
			grChoice.inactive = bInactive;
			var bSuccess = GlideRecordUpdate(grChoice);
			if (bSuccess == false)
			{
				this.Error(
					'ActivateChoice: Failed to set '+strTable+'.'+strElement+'."'+strValue+'".'+grChoice.language+' inactive to '+RenderValue(bInactive));
				return false;
			}

			this.Log(
				'ActivateChoice: Set '+strTable+'.'+strElement+'."'+strValue+'".'+grChoice.language+' inactive to '+RenderValue(bInactive));
		}

		return true;
	},

	ChoiceSetDependentValue: function(strReason,strTable,strElement,strValue,strDependentValue)
	{
		strDependentValue = GetStringValue(strDependentValue);
		if (IsValidTable(strTable) == false)
		{
			this.Error(
				'ChoiceSetDependentValue: Table "'+strTable+'" is invalid!');
			return false;
		}

		if (IsValidColumn(strTable,strElement) == false)
		{
			this.Error(
				'ChoiceSetDependentValue: Element '+strTable+'.'+strElement+' is invalid!');
			return false;

		}

		var grChoice = new GlideRecord('sys_choice');
		grChoice.addQuery('name',strTable);
		grChoice.addQuery('element',strElement);
		grChoice.addQuery('value',strValue);
		grChoice.query();
		if (grChoice.getRowCount() == 0)
		{
			this.Log(
				'ChoiceSetDependentValue: Did not find any choices for '+strTable+'.'+strElement+'."'+strValue+'" to set Dependent value to "'+strDependentValue+'"');
			return true;
		}

		this.Log(
			'ChoiceSetDependentValue: Attempting to set the Dependent value for all language versions of '+strTable+'.'+strElement+'."'+strValue+'" to "'+strDependentValue+'".');


		while (grChoice.next())
		{
			grChoice.dependent_value = strDependentValue;
			var bSuccess = GlideRecordUpdate(grChoice);
			if (bSuccess == false)
			{
				this.Error(
					'ChoiceSetDependentValue: Failed to set '+strTable+'.'+strElement+'."'+strValue+'".'+grChoice.language+' Dependent value to "'+strDependentValue+'"!');
				return false;
			}

			this.Log(
				'ChoiceSetDependentValue: Set '+strTable+'.'+strElement+'."'+strValue+'".'+grChoice.language+' Dependent value to "'+strDependentValue+'".');
		}

		return true;
	},



	DiscoverySourceInsertOrUpdate: function(strLabel,strValue)
	{
		return this.ChoiceInsertOrUpdate(
				'Insert or update discovery choice: "'+strLabel+'"',
				'cmdb_ci',
				'discovery_choice',
				'en',
				strLabel,
				strValue
			);
	},	

	DeleteView: function(strViewName)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		strViewName = GetStringValue(strViewName);

		var grView = new GlideRecord('sys_ui_view');
		grView.addQuery('name','=',GetStringValue(strViewName));
		grView.query();
		if (grView.getRowCount() == 0)
		{
			this.Log('View '+strViewName+' does not exist. No action required.');
			return true;
		}
		while (grView.next())
		{
			var bResult = RecordDelete('sys_ui_view',grView.sys_id);
			if (bResult == true)
			{
				this.Log('Deleted view: '+strViewName);
			}
			else
			{
				this.Error('Failed to delete view: '+strViewName);
				return false;
			}
		}
		return true;
	},	

	DeleteUISection: function(strView,strTable,strScope)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		strView = GetStringValue(strView);
		strTable = GetStringValue(strTable);
		strScope = GetStringValue(strScope);

		var grView = new GlideRecord('sys_ui_view');
		grView.addQuery('title','=',strView);
		grView.query();
		if (grView.getRowCount() != 1)
		{
			var grView = new GlideRecord('sys_ui_view');
			grView.addQuery('name','=',strView);
			grView.query();
			if (grView.getRowCount() != 1)
			{
				this.Log('View "'+strView+'" does not exist. No action required.');	
				return true;
			}
		}

		grView.next();

		var grSection = new GlideRecord('sys_ui_section');
		grSection.addQuery('name','=',strTable);
		grSection.addQuery('view','=',grView.sys_id);
		if (strScope != '')
		{
			grSection.addQuery('sys_scope','=',strScope);
		}
		grSection.query();
		if (grSection.getRowCount() == 0)
		{
			this.Log('View "'+strView+'" UI Section for table "'+strTable+'" does not exist. No action required.');
			return true;
		}
		while (grSection.next())
		{
			var bResult = RecordDelete('sys_ui_section',grSection.sys_id);
			if (bResult == true)
			{
				this.Log('Deleted UI Section in View "'+strView+'" for table "'+strTable+'".');
			}
			else
			{
				this.Error('Failed to delete UI Section in View "'+strView+'" for table "'+strTable+'".');
				return false;
			}
		}
		return true;
	},		


	InstallGlobalSystemProperty: function(strName, strType, strDescription)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var grSP = new GlideRecord('sys_properties');
		grSP.addQuery('name','=',GetStringValue(strName));
		grSP.query();
		if (grSP.next())
		{
			this.Log('Global system property '+strName+' already existst. No action required.');
			return grSP.getValue('sys_id');
		}

		var grSP = new GlideRecord('sys_properties');
		grSP.initialize();
		GlideRecordSetValue(grSP,'name',GetStringValue(strName));
		GlideRecordSetValue(grSP,'type',GetStringValue(strType));
		GlideRecordSetValue(grSP,'description',GetStringValue(strDescription));
		GlideRecordSetValue(grSP,'sys_scope','global');
		var strSysID = GlideRecordInsert(grSP);
		if (IsValidSysId(strSysID) == false)
		{
			this.Error('Failed to install global system property '+strName+'.');
			return false;
		}
		this.Log('Installed global system property '+strName+'.');
		return strSysID;
	},


	InstallSystemPropertyCategoryMapping: function(strSystemPropertyCategory, strSystemProperty, nOrder)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var grSPC = new GlideRecord('sys_properties_category');
		grSPC.addQuery('name','=',GetStringValue(strSystemPropertyCategory));
		grSPC.query();
		if (grSPC.next() == false)
		{
			this.Error('Failed to install system property category mapping. Category '+strSystemPropertyCategory+' does not exist.');
			return false;
		}
		var strSystemPropertyCategorySysId = grSPC.getValue('sys_id');

		var grSP = new GlideRecord('sys_properties');
		grSP.addQuery('name','=',GetStringValue(strSystemProperty));
		grSP.query();
		if (grSP.next() == false)
		{
			this.Error('Failed to install system property category mapping. System property '+strSystemProperty+' does not exist.');
			return false;
		}
		var strSystemPropertySysId = grSP.getValue('sys_id');


		var grSPCM2 = new GlideRecord('sys_properties_category_m2m');
		grSPCM2.addQuery('property','=',strSystemPropertySysId);
		grSPCM2.addQuery('category','=',strSystemPropertyCategorySysId);
		grSPCM2.query();
		if (grSPCM2.next())
		{
			this.Log('System property category mapping for property '+strSystemProperty+' to category '+strSystemPropertyCategory+' already exists. No action required.');
			return grSPCM2.getValue('sys_id');
		}	

		var grSPCM2 = new GlideRecord('sys_properties_category_m2m');
		grSPCM2.initialize();
		grSPCM2.sys_scope = grSPC.sys_scope;
		grSPCM2.category = strSystemPropertyCategorySysId;
		grSPCM2.property = strSystemPropertySysId;
		grSPCM2.order = nOrder;
		//grSPCM2.sys_scope = 'global';
		var strSysID = GetStringValue(GlideRecordInsert(grSPCM2));
		if (IsValidSysId(strSysID) == false)
		{
			this.Error('Failed to create system property category mapping for property '+strSystemProperty+' to category '+strSystemPropertyCategory+'.');
			return false;
		}
		this.Log('System property category mapping for property '+strSystemProperty+' to category '+strSystemPropertyCategory+' has been created.');
		return strSysID;
	},	

