	ShowFieldClass: function(strName,bVisible)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		strName = GetStringValue(strName);
		bVisible = GetBoolValue(bVisible);		

		var grSGO = new GlideRecord('sys_glide_object');
		grSGO.addQuery('name','=',strName);
		grSGO.query();
		if (grSGO.next() == false)
		{
			this.Log('Field Class "'+strName+'" does not exist. Doing nothing.');
			return false;
		}

		if (grSGO.visible == bVisible)
		{
			this.Log('Field Class "'+grSGO.name+'" is already set '+((bVisible)?('visible'):('ininvisible'))+'. No action required.');
			return GetStringValue(grSGO.sys_id);
		}
		grSGO.visible = bVisible;
		var vResult = GlideRecordUpdate(grSGO);
		if (vResult == false)
		{
			this.Error('Failed to set Field Class "'+grSGO.name+'" '+((bVisible)?('visible'):('invisible'))+'!');
			return false;
		}
		this.Log('Field Class "'+grSGO.name+'" is set '+((bVisible)?('visible'):('invisible'))+'.');
		return GetStringValue(grSGO.sys_id);		
	},		

	ActivateRecord: function(strSysId,bActive,strTable,strRecordClassName,strActiveField,strNameField,strAdditionalField)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		bActive = GetBoolValue(bActive);	
		strNameField = GetStringValue(strNameField);
		strAdditionalField = GetStringValue(strAdditionalField);

		var grRecord = GetRecord(strTable,strSysId);
		if (IsValidRecord(grRecord) == false)
		{
			this.Log(strRecordClassName+' with sys_id "'+strSysId+'" does not exist. No action required.');
			return false;
		}

		var strName = '"'+grRecord.getValue(strNameField)+'"';
		if (strAdditionalField != '')
		{
			strName += ' ('+grRecord.getValue(strAdditionalField)+')';
		}

		if (grRecord.getValue(strActiveField) == bActive)
		{
			this.Log(strRecordClassName+' '+strName+' is already set '+((bActive)?('active'):('inactive'))+'. No action required.');
			return strSysId;
		}
		grRecord.setValue(strActiveField,bActive);
		var vResult = GlideRecordUpdate(grRecord,true);
		if (vResult == false)
		{
			this.Error('Failed to set '+strRecordClassName+' '+strName+' '+((bActive)?('active'):('inactive'))+'!');
			return false;
		}
		this.Log(strRecordClassName+' '+strName+' is set '+((bActive)?('active'):('inactive'))+'.');
		return strSysId;		
	},

	InsertFieldToFormSection: function(strUiSectionSysId, strField, nPosition)
	{
		Deprecated();
		if (this.IsAborting() == true)
		{
			return false;
		}
		strField = GetStringValue(strField);
		nPosition = GetIntegerValue(nPosition);

		var grUiSection = GetRecord('sys_ui_section',strUiSectionSysId);
		if (IsValidRecord(grUiSection) == false)
		{
			this.Log('UI Section with sys_id "'+strUiSectionSysId+'" does not exist. Cannot add field "'+strField+'".');
			return false;
		}

		var strTable = GetStringValue(grUiSection.getDisplayValue('name'));

		var grUiElement = new GlideRecord('sys_ui_element');
		grUiElement.addQuery('sys_ui_section','=',strUiSectionSysId);
		grUiElement.addQuery('element','=',strField);
		grUiElement.query();
		if (grUiElement.getRowCount() > 0)
		{
			this.Log('UI Section on table "'+strTable+'" with sys_id "'+strUiSectionSysId+'" already contains field "'+strField+'". No action required.');
			return false;
		}

		var grUiElements = new GlideRecord('sys_ui_element');
		grUiElements.addQuery('sys_ui_section','=',strUiSectionSysId);
		grUiElements.addQuery('position','>=',nPosition);
		grUiElements.query();
		while (grUiElements.next())
		{
			grUiElements.position = grUiElements.position + 1;
			GlideRecordUpdate(grUiElements);
		}

		this.RecordInsertOrUpdate(
			'Add the "'+strField+'" field to a section of "'+strTable+'"',
			'sys_ui_element',
			{
				element: strField,
				sys_ui_section: strUiSectionSysId
			},
			{
				element: strField,
				sys_ui_section: strUiSectionSysId,
				position: nPosition
			},
			{});


	},

	DefineForm: function(vForm, aSections)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		aSections = GetArrayValue(aSections);

		var grForm = GetRecord('sys_ui_form',vForm);
		if (IsValidRecord(grForm) == false)
		{
			this.Log('Specified Form does not exist. Cannot re-define it.');
			return false;
		}

		var strTable = GetStringValue(grForm.getDisplayValue('name'));

		// Delete all existing section entries
		var grSectionEntries = new GlideRecord('sys_ui_form_section');
		grSectionEntries.addQuery('sys_ui_form','=',grForm.sys_id);
		grSectionEntries.query();
		GlideRecordDeleteMultiple(grSectionEntries);

		// Set the new Form Sections
		for (var nSection = 0; nSection < aSections.length; nSection++)
		{
			var vSection = aSections[nSection];
			var grSection = null;
			if (IsValidSysId(vSection) == true)
			{
				grSection = GetFormSectionRecord(vSection);
			}
			else
			{
				grSection = GetFormSectionRecord(grForm.view,strTable,vSection);
			}

			if (IsValidRecord(grSection) == false)
			{
				StringFormat('Failed to find section "{0}" for table "{1}" in view "{2}".',
						vSection,
						strTable,
						grForm.view.title);			
			}
			else
			{
				var grFormSectionEntry = new GlideRecord('sys_ui_form_section');
				grFormSectionEntry.initialize();
				grFormSectionEntry.sys_ui_form = grForm.sys_id;
				grFormSectionEntry.sys_ui_section = grSection.sys_id;
				grFormSectionEntry.position = nSection;

				var strFormSectionSysId = GlideRecordInsert(grFormSectionEntry);
				if (IsValidSysId(strFormSectionSysId) == false)
				{
					this.Log(
						StringFormat('Failed to add form section "{0}" for table "{1}" in view "{2}".',
							vSection,
							strTable,
							grForm.view.title));
					return false;
				}
			}
		}

		this.Log(
			StringFormat('Re-defined Form for table "{0}" in view "{1}".',
				strTable,
				grForm.view.title));

		return true;

	},


	DefineFormSection: function(vUiSection, aFields)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		aFields = GetArrayValue(aFields);

		var grUiSection = GetRecord('sys_ui_section',vUiSection);
		if (IsValidRecord(grUiSection) == false)
		{
			this.Log('Specified Form (UI) Section does not exist. Cannot re-define it.');
			return false;
		}

		var strTable = GetStringValue(grUiSection.getDisplayValue('name'));

		// Delete all existing fields
		var grUiElement = new GlideRecord('sys_ui_element');
		grUiElement.addQuery('sys_ui_section','=',grUiSection.sys_id);
		grUiElement.query();
		GlideRecordDeleteMultiple(grUiElement);

		// Set the new fields
		for (var nField = 0; nField < aFields.length; nField++)
		{
			var vField = aFields[nField];

			var strElement = '';
			var strType = '';
			var strSysUiFormatterSysId = '';
			if (IsObject(vField) == false)
			{
				strElement = GetStringValue(vField);
			}
			else
			{
				strElement = GetStringValue(GetValue(vField,'element'));
				strType = GetStringValue(GetValue(vField,'type'));
				strSysUiFormatterSysId = GetStringValue(GetValue(vField,'sys_ui_formatter'));
			}

			if (   strElement == '.begin_split'
				|| strElement == '.split'
				|| strElement == '.end_split')
			{
				strType = strElement;
			}

			if (IsValidSysId(strElement))
			{
				strType = 'annotation';
			}


			var grUiElement = new GlideRecord('sys_ui_element');
			grUiElement.initialize();
			grUiElement.sys_ui_section = grUiSection.sys_id;
			grUiElement.position = nField;
			grUiElement.element = strElement;
			grUiElement.type = strType;
			grUiElement.sys_ui_formatter = strSysUiFormatterSysId;
			var strUiElementSysId = GlideRecordInsert(grUiElement);
			if (IsValidSysId(strUiElementSysId) == false)
			{
				this.Log(
					StringFormat('Failed to re-define Form (UI) Section "{0}" for table "{1}" in view "{2}".',
						grUiSection.caption,
						grUiSection.name,
						grUiSection.view.title));
				return false;
			}

		}

		this.Log(
			StringFormat('Re-defined Form (UI) Section "{0}" for table "{1}" in view "{2}".',
				grUiSection.caption,
				grUiSection.name,
				grUiSection.view.title));

		return true;

	},

	DefineList: function(vView, strTable, aFields)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		aFields = GetArrayValue(aFields);

		var grUiView = GetRecord('sys_ui_view',vView);
		if (IsValidRecord(grUiView) == false)
		{
			this.Error('Specified View does not exist.');
			return false;
		}

		var grUiList = new GlideRecord('sys_ui_list');
		grUiList.addQuery('name','=',strTable);
		grUiList.addQuery('view','=',grUiView.sys_id);
		grUiList.addNullQuery('sys_user');
		grUiList.addNullQuery('relationship');
		grUiList.addNullQuery('parent');
		grUiList.query();
		if (grUiList.next() == false)
		{
			this.Error('Specified (UI) List does not exist. Cannot re-define it.');
			return false;
		}
		if (grUiList.getRowCount() != 1)
		{
			this.Error('Specified (UI) List is ambiguous. Cannot re-define it.');
			return false;
		}

		// Delete all existing fields
		var grUiListElement = new GlideRecord('sys_ui_list_element');
		grUiListElement.addQuery('list_id','=',grUiList.sys_id);
		grUiListElement.query();
		GlideRecordDeleteMultiple(grUiListElement);

		// Set the new fields
		for (var nField = 0; nField < aFields.length; nField++)
		{
			var vField = aFields[nField];

			var strElement = '';
			if (IsObject(vField) == false)
			{
				strElement = GetStringValue(vField);
			}
			else
			{
				strElement = GetStringValue(GetValue(vField,'element'));
			}

			var grUiListElement = new GlideRecord('sys_ui_list_element');
			grUiListElement.initialize();
			grUiListElement.list_id = grUiList.sys_id;
			grUiListElement.position = nField;
			grUiListElement.element = strElement;
			grUiListElement.average_value = GetValue(vField,'average_value');
			grUiListElement.max_value = GetValue(vField,'max_value');
			grUiListElement.min_value = GetValue(vField,'min_value');
			grUiListElement.sum = GetValue(vField,'sum');

			var strUiListElementSysId = GlideRecordInsert(grUiListElement);
			if (IsValidSysId(strUiListElementSysId) == false)
			{
				this.Error(
					StringFormat('Failed to re-define List (UI) for table "{0}" in view "{1}".',
						grUiList.name,
						grUiList.view.title));
				return false;
			}

		}

		this.Log(
			StringFormat('Re-defined List (UI) for table "{0}" in view "{1}".\nTHIS REQUIRES CACHE CLEARING TO TAKE EFFECT!',
				grUiList.name,
				grUiList.view.title));

		return true;

	},


	DefineRelatedList: function(vUiRelatedList, aRelatedLists)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}
		aRelatedLists = GetArrayValue(aRelatedLists);

		var grUiRelatedList = GetRecord('sys_ui_related_list',vUiRelatedList);
		if (IsValidRecord(grUiRelatedList) == false)
		{
			this.Error('Specified Related List does not exist. Cannot re-define it.');
			return false;
