		this.Log(
			StringFormat('Re-defined Related List for table "{0}" in view "{1}".',
				strTable,
				grUiRelatedList.view.title));

		return true;

	},

	DefineUiPolicyAction: function(
		vUiPolicy,
		strField,
		strMandatory,
		strVisible,
		strDisabled,
		strCleared)
	{
		if (this.IsAborting() == true)
		{
			return false;
		}

		var grUiPolicy = GetRecord('sys_ui_policy',vUiPolicy);
		if (IsValidRecord(grUiPolicy) == false)
		{
			this.Error('Specified UI Policy does not exist. Cannot define a UI Policy Action.');
			return false;
		}		

		strField = GetStringValue(strField);
		if (strField == '')
		{
			this.Error('Field not specified. Cannot define a UI Policy Action.');
			return false;
		}			

		var values = {};

		function GetUiActionValue(strValue)
		{
			if (strValue === null)
			{
				return 'ignore';
			}
			if (strValue == 'ignore')
			{
				return 'ignore';
			}
			return RenderValue(GetBoolValue(strValue));
		}

		values.mandatory = GetUiActionValue(strMandatory);
		values.visible = GetUiActionValue(strVisible);
		values.disabled = GetUiActionValue(strDisabled);
		values.cleared = GetBoolValue(strCleared);

		return this.RecordInsertOrUpdate(
			'Define a UI Policy Action for UI Policy "'+grUiPolicy.short_description+'" on table "'+grUiPolicy.table+'" on field "'+strField+'"',
			'sys_ui_policy_action',
			{
				ui_policy: GetStringValue(grUiPolicy.sys_id),
				field: strField,
				sys_scope: GetStringValue(grUiPolicy.sys_scope)
			},
			{},
			values);
	},


