	},

	AuditQueryRangeACLs: function()
	{
		var grScriptInclude = new GlideRecord('sys_script_include');
		grScriptInclude.addQuery('name','=','QueryRangeACLAuditor');
		grScriptInclude.query();
		if (grScriptInclude.next() == false)
		{
			this.Error('Fix for KB2046494 is not available on this instance. Cannot run QueryRangeACLAuditor::auditQueryRangeACLs().');
			return false;
		}

		return this.GenericAction(
			x_snc_devtools.RunScriptInGlobalScope(
				'result = new global.QueryRangeACLAuditor().auditQueryRangeACLs();'),
			'Fix described in KB2046494 has been applied successfully.',
			'Applying fix in KB2046494 failed.');
	},



