sys_script_include.script:
function IsForceScopeActiveForUser(vUser)
{
	if (AppGetProperty('ui.force_scope') == false)
	{
		return false;
	}


	var grUser = GetUserRecord(vUser)
	var strUserSysId = '';
	if (IsValidRecord(grUser) == true)
	{
		strUserSysId = GetStringValue(grUser.sys_id);
	}
	else
	{
		strUserSysId = gs.getUserID();
	}

	var strForceScope = GetUserPreference(strUserSysId,'devtools.ui.force_scope');
	if (strForceScope !== null)
	{
		bForceScope = GetBoolValue(strForceScope);
		if (bForceScope == false)
		{
			return false;
		}
	}

	return true;
}
